INSERT INTO `ttddnew1` (`id`, `symbol`, `kind`, `assunto`, `fase_corrente`, `fase_intermediaria`, `destinacao`, `observacoes`) VALUES
(1, '000', '', ' ADMINISTRAÇÃO GERAL', '', '', '', ' Esta classe contempla documentos referentes às atividades relacionadas à administração interna do órgão e entidade que viabilizam o seu funcionamento e o alcance dos objetivos para os quais foram criados.'),
(2, '001', '', ' RELAÇÃO INTERINSTITUCIONAL', '', '', '', ' Incluem-se documentos referentes à formalização, execução e acompanhamento das relações entre o órgão e entidade e outros órgãos e entidades, públicos e privados, firmadas por meio de acordos, contratos, convênios, termos e outros atos de ajustes, que abranjam, ao mesmo tempo, a execução de várias atividades, bem como aqueles referentes à fiscalização, prestação e tomada de contas, relatórios técnicos e termos de aditamento.'),
(3, '002', '', ' ATENDIMENTO AO CIDADÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às solicitações de informações e às comunicações enviadas ao órgão e entidade pelos canais de atendimento ao cidadão, tais como: Serviço de Informações ao Cidadão (SIC), ouvidoria e outros canais de comunicação.'),
(4, '002.01', '', ' NORMATIZAÇÃO. REGULAMENTAÇÃO', '', '', '', ' Incluem-se documentos referentes às determinações legais, aos atos e instruções normativas, aos procedimentos operacionais e às decisões de caráter geral sobre o acesso à informação pública executado pelos canais de atendimento ao cidadão.'),
(5, '002.1', '', ' ACESSO À INFORMAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos pedidos de acesso à informação e aos documentos institucionais, encaminhados ao SIC, bem como os recursos impetrados em razão de negativa de acesso. Quanto ao controle de satisfação dos usuários em relação aos canais de atendimento ao cidadão, classificar no código 002.2. Quanto tratamento de demandas recebidas pela ouvidoria e por outros canais de comunicação, classificar nas subdivisões do código 002.3. Quanto às comunicações eventuais trocadas entre o órgão e entidade e demais instituições, classificar no código 991.'),
(6, '002.11', '', ' PEDIDO DE ACESSO À INFORMAÇÃO E RECURSO', '', '', '', ' Incluem-se documentos referentes às solicitações, respostas e recursos referentes aos pedidos de acesso à informação, presenciais e não presenciais, realizados por intermédio do SIC, tais como: formulários com pedidos de acesso à informação e formulários de recursos. Quanto ao acesso e ao controle das consultas aos documentos arquivísticos, bibliográficos e museológicos, classificar no código 063.1.'),
(7, '002.12', '', ' ACOMPANHAMENTO DO ATENDIMENTO AO CIDADÃO', '', '', '', ' Incluem-se documentos referentes ao acompanhamento das atividades desempenhadas pelo SIC, tais como: relatórios estatísticos, de atendimento, de controle de consultas e de perfil do usuário.'),
(8, '002.2', '', ' CONTROLE DE SATISFAÇÃO DO USUÁRIO', '', '', '', ' Incluem-se documentos referentes à pesquisa de satisfação dos usuários dos serviços públicos, decorrentes de pedidos de acesso à informação e de manifestações registradas na ouvidoria e nos demais canais de comunicação do órgão e entidade. Quanto aos pedidos de acesso à informação e aos documentos institucionais, classificar nas subdivisões do código 002.1. Quanto ao tratamento das demandas recebidas pela ouvidoria e por outros canais de comunicação, classificar nas subdivisões do código 002.3. Quanto às comunicações eventuais trocadas entre o órgão e entidade e demais instituições, classificar no código 991.'),
(9, '002.3', '', ' ACOMPANHAMENTO DE MANIFESTAÇÃO DO USUÁRIO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao acompanhamento e tratamento de manifestações de usuários do órgão e entidade, recebidas pela ouvidoria e demais canais de comunicação institucional, abrangendo as reclamações, elogios, solicitações, sugestões ou denúncias, bem como às ações de ouvidoria ativa. Quanto aos pedidos de acesso à informação e aos documentos institucionais, classificar nas subdivisões do código 002.1. Quanto à conversão da demanda em processo administrativo, classificar de acordo com a atividade decorrente da manifestação ou da denúncia.'),
(10, '002.31', '', ' TRATAMENTO DE MANIFESTAÇÃO', '', '', '', ' Incluem-se documentos referentes à análise e tratamento de manifestações recebidas pela ouvidoria e demais canais de comunicação do órgão ou entidade, pertinentes às reclamações, elogios, solicitações e sugestões de usuários. Quanto ao tratamento de denúncias e comunicações de ilegalidade ou irregularidade, classificar no código 002.32.'),
(11, '002.32', '', ' TRATAMENTO DE DENÚNCIA', '', '', '', ' Incluem-se documentos referentes à análise e tratamento de denúncias e comunicações de irregularidade ou ilegalidade, recebidas pela ouvidoria e demais canais de comunicação do órgão ou entidade. Quanto ao tratamento de reclamações, elogios, solicitações e sugestões, classificar no código 002.31.'),
(12, '002.33', '', ' DESENVOLVIMENTO DE AÇÕES DE OUVIDORIA ATIVA', '', '', '', ' Incluem-se documentos referentes ao planejamento e execução de ações proativas de ouvidoria, para coleta de manifestações junto aos usuários, no tocante à qualidade dos serviços do órgão ou entidade.'),
(13, '003', '', ' CONTROLE E FISCALIZAÇÃO DA GESTÃO PÚBLICA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às ações de inspeção, correição, auditagem e controle interno a cargo do gestor, realizadas para verificação dos procedimentos internos do órgão e entidade com o objetivo de melhorar a gestão pública.'),
(14, '003.01', '', ' NORMATIZAÇÃO. REGULAMENTAÇÃO', '', '', '', ' Incluem-se documentos referentes às determinações legais, aos atos e instruções normativas, aos procedimentos operacionais e às decisões de caráter geral sobre as ações de inspeção, correição, auditagem e controle interno do órgão e entidade. Quanto ao controle externo e à auditoria externa, classificar nas subdivisões do código 054.'),
(15, '003.1', '', ' CONTROLE INTERNO. AUDITORIA INTERNA', '', '', '', ' Incluem-se documentos referentes à avaliação das ações executadas pelo órgão e entidade, da legalidade dos procedimentos e da atuação do gestor público.'),
(16, '003.2', '', ' AÇÃO PREVENTIVA', '', '', '', ' Incluem-se documentos referentes às ações implementadas por orientação dos órgãos fiscalizadores para prevenção da corrupção no órgão e entidade.'),
(17, '003.3', '', ' CORREIÇÃO', '', '', '', ' Incluem-se documentos referentes à fiscalização e apuração de responsabilidades realizadas pelos órgãos fiscalizadores em caso de irregularidades cometidas no órgão e entidade.'),
(18, '004', '', ' ASSESSORAMENTO JURÍDICO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à análise jurídica de instrumentos e de atos normativos e ao acompanhamento de ações judiciais.'),
(19, '004.1', '', ' ORIENTAÇÃO TÉCNICA E NORMATIVA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à análise jurídica de instrumentos e de atos normativos elaborados pelo órgão e entidade.'),
(20, '004.11', '', ' UNIFORMIZAÇÃO DO ENTENDIMENTO JURÍDICO', '', '', '', ' Incluem-se documentos referentes à análise e à fixação de interpretação da Constituição, das leis, dos tratados e dos demais atos normativos, a serem seguidos, de modo uniforme, pelo órgão e entidade, quando não houver orientação normativa superior, tais como: instruções, pareceres e notas.'),
(21, '004.12', '', ' ANÁLISE DOS INSTRUMENTOS ADMINISTRATIVOS', '', '', '', ' Incluem-se documentos referentes ao exame e à análise prévia ou conclusiva de textos de editais de licitação, de contratos e de instrumentos congêneres a serem publicados e celebrados pelo órgão e entidade.'),
(22, '004.2', '', ' ATUAÇÃO EM CONTENCIOSO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à atuação contenciosa judicial e administrativa, nas hipóteses de contestação, conflito ou litígio, exercida pela assessoria ou consultoria jurídica do órgão e entidade.'),
(23, '004.21', '', ' REPRESENTAÇÃO EXTRAJUDICIAL', '', '', '', ' Incluem-se documentos referentes à solução extrajudicial dos litígios, visando a composição entre as partes em conflito de interesses, por meio de mediação, conciliação, arbitragem e demais técnicas de composição e administração de conflitos.'),
(24, '004.22', '', ' REPRESENTAÇÃO JUDICIAL', '', '', '', ' Incluem-se documentos referentes às ações defendidas pela assessoria ou consultoria jurídica em processos administrativos e judiciais, perante todas as instâncias, ordinárias ou extraordinárias, utilizando as medidas capazes de propiciar a efetiva defesa dos interesses do órgão e entidade.'),
(25, '005', '', ' PARTICIPAÇÃO EM ÓRGÃOS COLEGIADOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à organização e ao funcionamento de colegiados, como comissões, conselhos, comitês, juntas e grupos de trabalho, criados pelo próprio órgão e entidade ou por outros órgãos de deliberação coletiva, que contem com a participação de servidores da instituição. Quanto aos documentos referentes às atividades do órgão colegiado, classificar no descritor referente ao objeto de sua atuação. Quanto aos documentos referentes ao processo eletivo, classificar nas subdivisões do código 014.'),
(26, '005.1', '', ' CRIAÇÃO E ORGANIZAÇÃO', '', '', '', ' Incluem-se documentos referentes à implantação de órgãos colegiados, tais como: ato de instituição, regras para atuação, designação e substituição de membros. Quanto aos documentos referentes à atuação do órgão colegiado, devem ser classificados no descritor referente ao objeto de sua atuação.'),
(27, '005.2', '', ' OPERACIONALIZAÇÃO DE REUNIÕES', '', '', '', ' Incluem-se documentos referentes à organização das reuniões dos órgãos colegiados, bem como aqueles referentes ao agendamento, convocação, pauta e lista de participantes.'),
(28, '010', '', ' ORGANIZAÇÃO E FUNCIONAMENTO', '', '', '', ' Esta subclasse contempla documentos referentes à definição e alteração das políticas institucionais, à criação e modificação das estruturas organizacionais e aos registros que garantam a existência do órgão e entidade como pessoa jurídica e a sua atuação no meio público, privado, com o terceiro setor e com o cidadão, bem como aqueles referentes à contratação de prestação de serviços para o funcionamento do órgão e entidade, e ao planejamento e acompanhamento das ações institucionais, da gestão ambiental e da comunicação social.'),
(29, '010.01', '', ' NORMATIZAÇÃO. REGULAMENTAÇÃO', '', '', '', ' Incluem-se documentos referentes às determinações legais, aos atos e instruções normativas, aos procedimentos operacionais internos e às decisões de caráter geral sobre a organização e funcionamento de todas as atividades do órgão e entidade, bem como os boletins administrativos e de serviço. Quanto aos boletins de pessoal, classificar no código 020.01. Quanto à publicação de matérias em boletins administrativos, de serviço, de pessoal, em diários oficiais e em periódicos de grande circulação, classificar no código 069.3. Quanto a documentos referentes à função de normatização e regulamentação de determinada atividade que alcancem os órgãos e entidades do Poder Executivo federal, classificar na atividade finalística.'),
(30, '011', '', ' ORGANIZAÇÃO ADMINISTRATIVA', '', '', '', ' Incluem-se documentos referentes aos estudos para a definição da estrutura e das atribuições do órgão e entidade e para as mudanças estratégicas (missão, finalidade e forma de atuação) e estruturais (hierarquia, distribuição formal de autoridade e responsabilidade, abertura ou encerramento de unidades administrativas), bem como aqueles resultantes da implantação de reformas administrativas ou de processos de modernização, com impacto no órgão e entidade, na forma de fusão, privatização, reestatização ou extinção.'),
(31, '012', '', ' HABILITAÇÃO JURÍDICA E REGULARIZAÇÃO FISCAL', '', '', '', ' Incluem-se documentos referentes à inscrição, baixa e cancelamento nos órgãos competentes, tais como: cadastro bancário, registros de inscrição no Cadastro Nacional de Pessoa Jurídica (CNPJ), no Sistema de Operações, Registro e Controle do Banco Central (Sisbacen), no Serviço de Proteção ao Crédito (SPC), no Cadastro Informativo de Créditos e não Quitados do Setor Público Federal (Cadin).'),
(32, '013', '', ' COORDENAÇÃO E GESTÃO DE REUNIÕES', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos relativos às atividades necessárias para a realização de assembleias, audiências, despachos e reuniões, gerais e setoriais, do órgão e entidade, bem como aqueles referentes ao registro de suas deliberações.'),
(33, '013.1', '', ' OPERACIONALIZAÇÃO', '', '', '', ' Incluem-se documentos referentes à organização das reuniões do órgão e entidade, bem como aqueles referentes ao agendamento, convocação, pauta e lista de participantes.'),
(34, '013.2', '', ' REGISTRO DE DELIBERAÇÕES', '', '', '', ' Incluem-se documentos referentes aos registros das deliberações e tomadas de decisão definidas nas assembleias, audiências, despachos e reuniões, tais como: atas e relatórios.'),
(35, '014', '', ' GERENCIAMENTO DE PROCESSO ELETIVO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos processos eletivos para preenchimento de função de direção, eleição para atuação em órgãos colegiados e demais consultas públicas ou institucionais, previstos em legislação competente à área de atuação do órgão e entidade.'),
(36, '014.1', '', ' NOMEAÇÃO E ATUAÇÃO DA COMISSÃO ELEITORAL', '', '', '', ' Incluem-se documentos referentes à constituição e às atividades da comissão eleitoral, das mesas de votação e dos fiscais, tais como: indicação dos membros, regimento eleitoral, convocação, pauta, lista de participantes e registros e atas das reuniões.'),
(37, '014.2', '', ' INSCRIÇÃO', '', '', '', ' Incluem-se documentos referentes à definição do calendário eleitoral, à inscrição de candidatos para participação no processo eletivo e à divulgação das atividades eletivas e dos candidatos, tais como: cronograma, cópia de documentos pessoais dos inscritos ou integrantes das chapas concorrentes, propaganda eleitoral e pedidos de impugnação de candidatura.'),
(38, '014.3', '', ' VOTAÇÃO', '', '', '', ' Incluem-se documentos referentes à apuração do processo eletivo, tais como: relação de eleitores habilitados, cédulas de votação e contagem de votos.'),
(39, '014.4', '', ' DIVULGAÇÃO DOS RESULTADOS E INTERPOSIÇÃO DE RECURSOS', '', '', '', ' Incluem-se documentos referentes aos resultados finais da eleição realizada e à divulgação dos candidatos eleitos, bem como aqueles referentes aos recursos impetrados.'),
(40, '015', '', ' GESTÃO INSTITUCIONAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao planejamento, acompanhamento, avaliação, governança e acreditação das atividades do órgão e entidade.'),
(41, '015.1', '', ' PLANEJAMENTO INSTITUCIONAL', '', '', '', ' Incluem-se documentos referentes ao planejamento, às ações e aos programas e projetos de trabalho do órgão e entidade, tais como: planejamento plurianual, planejamento estratégico, plano de desenvolvimento institucional, plano de metas e definição de indicadores de desempenho.'),
(42, '015.2', '', ' ACOMPANHAMENTO DAS ATIVIDADES', '', '', '', ' Incluem-se documentos referentes aos registros das atividades desempenhadas pelo órgão e entidade, tais como: relatórios parciais (mensal, trimestral ou semestral), relatório anual e relatório de gestão.'),
(43, '015.3', '', ' AVALIAÇÃO DA GESTÃO INSTITUCIONAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às ações de autoavaliação, para verificação do desempenho do órgão e entidade, visando o controle da qualidade e a melhoria na prestação do serviço público.'),
(44, '015.31', '', ' ELABORAÇÃO DOS INSTRUMENTOS DE AVALIAÇÃO', '', '', '', ' Incluem-se documentos referentes ao planejamento das atividades de avaliação e aos programas e projetos de implementação do controle da qualidade da gestão do órgão e entidade, tais como: definição de indicadores e de instrumentos para avaliação dos aspectos gerenciais, diagnósticos e cronogramas.'),
(45, '015.32', '', ' EXECUÇÃO E ACOMPANHAMENTO', '', '', '', ' Incluem-se documentos referentes à implementação das atividades de avaliação e controle da qualidade da gestão institucional, bem como aqueles referentes à análise crítica e à verificação da compatibilidade entre o planejamento e os resultados obtidos na apuração das metas institucionais.'),
(46, '015.33', '', ' CERTIFICAÇÃO DA CONFORMIDADE', '', '', '', ' Incluem-se documentos referentes à certificação da conformidade, às propostas de ações corretivas e preventivas e ao tratamento da não conformidade, bem como relatórios estatísticos, demonstrativos de resultados, certificados e premiações.'),
(47, '015.4', '', ' GOVERNANÇA', '', '', '', ' Nas subdivisões desse descritor classificam-se documentos referentes ao monitoramento de riscos, a integridade e o controle interno, visando dar transparência, equidade e responsabilização aos órgãos e entidades.'),
(48, '015.41', '', ' GERENCIAMENTO DE RISCO', '', '', '', ' Incluem-se documentos referentes aos procedimentos de identificação, análise, avaliação, tratamento e monitoramento dos riscos que podem afetar o alcance dos objetivos dos órgãos e entidades.'),
(49, '015.42', '', ' INTEGRIDADE E CONTROLE INTERNO', '', '', '', ' Incluem-se documentos referentes a procedimentos internos adotados pelo órgão ou entidade com a intenção de prevenir, detectar e combater a corrupção e fraudes, garantindo a integridade do processo de tomada de decisão e da prática de atos de gestão.'),
(50, '015.5', '', ' ACOMPANHAMENTO DA ACREDITAÇÃO', '', '', '', ' Incluem-se documentos referentes à solicitação, obtenção e manutenção da acreditação, alusivo ao atendimento de requisitos previamente definidos para realizar as atividades com confiança, em seu âmbito de atuação.'),
(51, '016', '', ' GESTÃO DE PROCESSOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às etapas que envolvem planejamento, análise, mapeamento, desenho e modelagem de processos institucionais e ao gerenciamento de desempenho.'),
(52, '016.1', '', ' PLANEJAMENTO DO MAPEAMENTO DE PROCESSOS', '', '', '', ' Incluem-se documentos referentes ao planejamento do mapeamento de processos institucionais, tais como: identificação de objetivos e de ferramentas a serem utilizadas e cronograma de atividades.'),
(53, '016.2', '', ' EXECUÇÃO E ACOMPANHAMENTO', '', '', '', ' Incluem-se documentos referentes ao desenvolvimento do mapeamento de processos institucionais e à coleta de dados.'),
(54, '016.3', '', ' RESULTADO', '', '', '', ' Incluem-se documentos referentes aos resultados do mapeamento de processos institucionais, tais como: diagnósticos, fluxogramas e relatórios.'),
(55, '016.4', '', ' MODELAGEM DE PROCESSOS', '', '', '', ' Incluem-se documentos referentes à definição de especificações para a modelagem de processos novos ou modificados.'),
(56, '016.5', '', ' GERENCIAMENTO DE DESEMPENHO', '', '', '', ' Incluem-se documentos referentes à medição e monitoramento de processos e propostas de ações corretivas ou preventivas, tais como: questionários, avaliações, propostas, análises e laudos.'),
(57, '017', '', ' GESTÃO AMBIENTAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à gestão ambiental, visando à utilização racional e sustentável dos recursos naturais, envolvendo o uso de práticas e o desenvolvimento de hábitos que garantam a proteção, conservação e preservação da biodiversidade, a reciclagem das matériasprimas e a redução do impacto ambiental.'),
(58, '017.1', '', ' PROTEÇÃO AMBIENTAL INTERNA', '', '', '', ' Incluem-se documentos referentes aos procedimentos de controle e preservação do ambiente de trabalho que envolve a conscientização dos servidores, tais como: campanhas de conscientização para redução do consumo de água e energia elétrica e programas de coleta seletiva solidária e reciclagem de resíduos descartáveis, bem como a produção de material de divulgação. Quanto ao controle de riscos ambientais, classificar no código 025.21. Quanto ao recolhimento de material inservível e de sucatas ao depósito, classificar no código 032.3.'),
(59, '017.2', '', ' PROTEÇÃO AMBIENTAL EXTERNA', '', '', '', ' Incluem-se documentos referentes aos procedimentos de controle e preservação ambiental externa que envolve a coleta seletiva solidária, a reciclagem de resíduos descartáveis e o uso de fontes não poluentes, tais como: projetos, questionários, avaliações, análises, laudos, relatórios estatísticos e de destinação de resíduos. Quanto à alienação definitiva, por desfazimento, de material permanente e de consumo em razão de serem considerados inservíveis e irrecuperáveis, classificar nos códigos 033.41 e 033.42, respectivamente.'),
(60, '018', '', ' CONTRATAÇÃO DE PRESTAÇÃO DE SERVIÇOS', '', '', '', ' Nas subdivisões desse descritor classificam-se documentos referentes à contratação de pessoa jurídica ou pessoa física para a realização de serviços e/ou fornecimento de mão de obra terceirizada para o órgão ou entidade, com exceção dos referentes à execução de obras em bens imóveis. Quanto à contratação de pessoa jurídica ou física para execução de obras em bens imóveis do órgão e entidade, classificar nas subdivisões do código 045.3.'),
(61, '018.1', '', ' CONTRATAÇÃO DE PESSOA JURÍDICA', '', '', '', ' Incluem-se documentos referentes à contratação de pessoa jurídica para a realização de serviços e/ou fornecimento de mão de obra para o órgão ou entidade, tais como: planejamento da contratação, divulgação, seleção do fornecedor, contrato, indicação e designação do gestor e dos fiscais da execução do contrato para acompanhamento, fiscalização, avaliação e aferição dos resultados previstos na contratação e demais documentos comprobatórios da prestação de serviços.'),
(62, '018.2', '', ' CONTRATAÇÃO DE PESSOA FÍSICA', '', '', '', ' Incluem-se documentos referentes à contratação de pessoas físicas (autônomos e colaboradores) para a realização de serviços técnicos profissionais especializados, transitórios, de caráter eventual, por prazo determinado e sem vínculo empregatício, tais como: planejamento da contratação, divulgação, seleção, contrato, indicação e designação do gestor e dos fiscais da execução do contrato para acompanhamento, fiscalização, avaliação e aferição dos resultados previstos na contratação e demais documentos comprobatórios da prestação de serviços.'),
(63, '019', '', ' OUTRAS AÇÕES REFERENTES À ORGANIZAÇÃO E FUNCIONAMENTO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes a outras ações de organização e funcionamento não contempladas nos descritores anteriores.'),
(64, '019.1', '', ' GERENCIAMENTO DE ESTRATÉGIAS DE MARKETING E DE COMUNICAÇÃO SOCIAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à administração da comunicação, à divulgação interna, ao planejamento, elaboração, acompanhamento, execução e avaliação das estratégias e ações de marketing (como o cultural, social, ambiental, de relacionamento, institucional e endomarketing) e de merchandising, alinhadas com o posicionamento institucional, desenvolvidos com meios próprios, por meio da contratação de empresas terceirizadas ou profissionais transitórios, bem como às atividades de comunicação social nos âmbitos externo e interno, compreendendo a escolha e os usos de mídias empregadas. Quanto aos documentos referentes à contratação de empresas terceirizadas ou de autônomo, classificar nas subdivisões do código 018.'),
(65, '019.11', '', ' ADMINISTRAÇÃO DA COMUNICAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao credenciamento de jornalistas, relacionamento com a imprensa, elaboração de propagandas e campanhas publicitárias, elaboração e atualização de site institucional. Quanto ao assessoramento de cerimonial para a realização de solenidades oficiais e eventos do órgão e entidade, classificar nas subdivisões do código 910.'),
(66, '019.111', '', ' CREDENCIAMENTO DE JORNALISTAS', '', '', '', ' Incluem-se documentos referentes ao credenciamento de jornalistas, tais como: normas de credenciamento, formulários e credenciais.'),
(67, '019.112', '', ' RELAÇÃO COM A IMPRENSA', '', '', '', ' Incluem-se documentos referentes à releases para publicação, mensagens, entrevistas, noticiários, reportagens e editoriais.'),
(68, '019.113', '', ' ELABORAÇÃO DE PROPAGANDAS, CAMPANHAS DE MARKETING E PUBLICITÁRIAS', '', '', '', ' Incluem-se documentos referentes às ações de marketing (como o cultural, social, ambiental, de relacionamento, institucional e endomarketing), ações de merchandising, campanhas institucionais, filmes, propagandas e à produção de material de divulgação do órgão e entidade, desenvolvido com meios próprios, por meio de contratação de empresas terceirizadas ou profissionais transitórios, bem como os produtos resultantes da elaboração. Quanto aos documentos referentes à contratação de empresas terceirizadas ou de autônomos, classificar nas subdivisões do código 018.'),
(69, '019.114', '', ' ELABORAÇÃO E ATUALIZAÇÃO DE SITE INSTITUCIONAL', '', '', '', ' Incluem-se documentos referentes à elaboração, atualização e publicação de conteúdos, postagens e interações no site institucional, bem como em mídias e redes sociais eletrônicas. Quanto aos documentos referentes ao desenvolvimento e manutenção dos sites, pertinente aos requisitos informáticos, classificar nas subdivisões do código 066.'),
(70, '019.12', '', ' DIVULGAÇÃO INTERNA', '', '', '', ' Incluem-se documentos referentes à divulgação para o público interno de notícias publicadas que envolvem o órgão e entidade ou sobre assuntos afins à sua missão, tais como: clipagem de notícias veiculadas em jornais, em revistas e em sites de comunicação e divulgação de avisos, comunicados, boletins e informativos.'),
(71, '019.13', '', ' PLANEJAMENTO, ELABORAÇÃO E ACOMPANHAMENTO', '', '', '', ' Incluem-se documentos referentes ao planejamento, elaboração e ao acompanhamento das estratégias e ações de marketing e de comunicação social, desenvolvidas pelo órgão ou entidade.'),
(72, '019.14', '', ' EXECUÇÃO E AVALIAÇÃO DE ESTRATÉGIAS', '', '', '', ' Incluem-se documentos referentes à execução e avaliação das estratégias e ações de marketing e de comunicação social, desenvolvidas pelo órgão ou entidade.'),
(73, '019.2', '', ' AÇÃO DE RESPONSABILIDADE SOCIAL', '', '', '', ' Incluem-se documentos referentes à participação em ações de incentivo ao esporte, à cultura e à educação, com possibilidade de dedução de imposto de renda, conforme especificado em legislação. Quanto às ações de marketing e de merchandising, classificar no código 019.113.'),
(74, '020', '', ' GESTÃO DE PESSOAS', '', '', '', ' Esta subclasse contempla documentos referentes aos direitos e obrigações dos servidores e empregados públicos, dos servidores temporários, dos residentes (aqueles inscritos nas residências médica, multiprofissional em saúde, pedagógica e jurídica, entre outras), dos estagiários, dos ocupantes de cargo comissionado e de função de confiança sem vínculo, lotados no órgão e entidade, bem como aqueles referentes aos direitos e obrigações do empregador.'),
(75, '020.01', '', ' NORMATIZAÇÃO. REGULAMENTAÇÃO', '', '', '', ' Incluem-se documentos referentes às determinações legais, aos atos e instruções normativas, aos procedimentos operacionais e às decisões de caráter geral sobre a gestão de pessoas do órgão e entidade, bem como os boletins de pessoal. Quanto aos boletins administrativos e de serviço, classificar no código 010.01. Quanto à publicação de matérias em boletins administrativos, de serviço, de pessoal, em diários oficiais e em periódicos de grande circulação, classificar no código 069.3.'),
(76, '020.02', '', ' IMPLEMENTAÇÃO DAS POLÍTICAS DE PESSOAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao planejamento, desenvolvimento e implantação das políticas de pessoal.'),
(77, '020.021', '', ' PLANEJAMENTO DA FORÇA DE TRABALHO. PREVISÃO DE PESSOAL', '', '', '', ' Incluem-se documentos referentes ao levantamento das habilidades e especificações necessárias para o exercício das funções e atividades rotineiras e eventuais, visando subsidiar a previsão de pessoal, definindo qualificação e quantitativo.'),
(78, '020.022', '', ' CRIAÇÃO, EXTINÇÃO, CLASSIFICAÇÃO, TRANSFORMAÇÃO E TRANSPOSIÇÃO DE CARGOS E DE CARREIRAS', '', '', '', ' Incluem-se documentos referentes à criação, transformação e reestruturação de cargos, carreiras e remuneração dos servidores da administração pública. Quanto à reestruturação e alteração salarial, classificar no código 023.12.'),
(79, '020.03', '', ' RELACIONAMENTO COM ENTIDADES REPRESENTATIVAS DE CLASSE', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às negociações que envolvem servidores, sindicatos e empregados, bem como aqueles referentes ao relacionamento do órgão e entidade com os conselhos profissionais.'),
(80, '020.031', '', ' RELAÇÃO COM SINDICATOS', '', '', '', ' Incluem-se documentos referentes aos acordos, convenções e dissídios coletivos estabelecidos nas negociações que envolvem servidores, sindicatos e empregadores. Quanto à contribuição sindical, assistencial e confederativa do servidor, classificar no código 023.171. Quanto à contribuição sindical do empregador, classificar no código 023.183.'),
(81, '020.032', '', ' MOVIMENTOS REIVINDICATÓRIOS. GREVES. PARALISAÇÕES', '', '', '', ' Incluem-se documentos referentes à notificação da greve, por parte dos grevistas, ao órgão e entidade, bem como aqueles referentes aos acordos e respectivas compensações por parte dos servidores sobre o desconto dos dias parados.'),
(82, '020.033', '', ' RELAÇÃO COM CONSELHOS PROFISSIONAIS', '', '', '', ' Incluem-se documentos referentes ao relacionamento com os conselhos profissionais, tais como: fiscalização do órgão e entidade pelos conselhos profissionais e comprovantes de pagamento da anuidade.'),
(83, '020.1', '', ' ASSENTAMENTO FUNCIONAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à vida funcional dos servidores e empregados públicos, dos servidores temporários, dos residentes (aqueles inscritos nas residências médica, multiprofissional em saúde, pedagógica e jurídica, entre outras), dos estagiários, dos ocupantes de cargo comissionado e de função de confiança sem vínculo, bem como os registros e as anotações dos atos da administração pública a que tiveram direito ou lhe foram impostos como deveres.'),
(84, '020.11', '', ' SERVIDORES E EMPREGADOS PÚBLICOS', '', '', '', ' Incluem-se documentos referentes à vida funcional dos servidores estatutários ativos (ou seja, ocupantes de cargos públicos) e inativos e dos empregados públicos (ou seja, ocupantes de empregos públicos) que são contratados e submetidos ao regime da legislação trabalhista.'),
(85, '020.12', '', ' SERVIDORES TEMPORÁRIOS', '', '', '', ' Incluem-se documentos referentes à vida funcional dos servidores que são contratados por tempo determinado, em caráter excepcional para atender uma eventual necessidade de interesse público, sem que estejam vinculados a cargo ou emprego público. Quanto à contratação de pessoas físicas (autônomos e colaboradores), classificar no código 018.2.'),
(86, '020.13', '', ' RESIDENTES E ESTAGIÁRIOS', '', '', '', ' Incluem-se documentos referentes à vida funcional dos residentes e dos estagiários.'),
(87, '020.14', '', ' OCUPANTES DE CARGO COMISSIONADO E DE FUNÇÃO DE CONFIANÇA', '', '', '', ' Incluem-se documentos referentes à vida funcional dos ocupantes de cargo comissionado e de função de confiança sem vínculo.'),
(88, '020.2', '', ' IDENTIFICAÇÃO FUNCIONAL', '', '', '', ' Incluem-se documentos referentes à requisição e ao controle de entrega de documentos de identificação funcional, tais como: carteira, cartão, identidade, crachá, credencial, passaporte de serviço ou diplomático.'),
(89, '021', '', ' RECRUTAMENTO E SELEÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos procedimentos desenvolvidos para a realização de concursos públicos para o provimento de cargos públicos, empregos públicos e contratação por tempo determinado ou para realização de processo seletivo, que ocorre entre instituições, para recrutamento de servidores e empregados públicos.'),
(90, '021.1', '', ' PLANEJAMENTO DO PROCESSO SELETIVO', '', '', '', ' Incluem-se estudos, propostas, constituição de bancas examinadoras, programas, editais, exemplares únicos de provas, gabaritos e critérios para correção de provas e para solicitação de recursos.'),
(91, '021.2', '', ' INSCRIÇÃO', '', '', '', ' Incluem-se documentos exigidos no edital para a homologação da inscrição e fichas de inscrição.'),
(92, '021.3', '', ' CONTROLE DE APLICAÇÃO DE PROVAS', '', '', '', ' Incluem-se documentos referentes ao controle de aplicação das provas, de acordo com os requisitos estipulados no edital.'),
(93, '021.4', '', ' CORREÇÃO DE PROVAS. AVALIAÇÃO', '', '', '', ' Incluem-se cadernos de prova utilizados pelos candidatos, folhas de resposta, provas de títulos, avaliação psicológica, testes psicotécnicos, exames médicos e de aptidão física, bem como currículos e entrevistas dos candidatos. Quanto às provas de títulos, avaliação psicológica, testes psicotécnicos, exames médicos e de aptidão física dos candidatos que vierem a ser nomeados, classificar nas subdivisões do código 020.1.'),
(94, '021.5', '', ' DIVULGAÇÃO DOS RESULTADOS E INTERPOSIÇÃO DE RECURSOS', '', '', '', ' Incluem-se documentos referentes aos resultados das provas realizadas ou do processo seletivo interno, a classificação e a reclassificação dos candidatos, bem como aqueles referentes aos recursos impetrados em qualquer uma das fases do concurso.'),
(95, '022', '', ' PROVIMENTO, MOVIMENTAÇÃO E VACÂNCIA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos procedimentos que efetivam as ações e as formas de ingresso, movimentação e desligamento na administração pública, bem como aqueles referentes às avaliações de desempenho dos servidores. Quanto aos atos específicos e individuais que deverão integrar o assentamento funcional, classificar nas subdivisões do código 020.1.'),
(96, '022.1', '', ' PROVIMENTO DE CARGO PÚBLICO', '', '', '', ' Incluem-se documentos referentes aos procedimentos que efetivam as ações de admissão, contratação, nomeação, designação, posse, disponibilidade, aproveitamento, readmissão, readaptação, recondução, reintegração, reversão e promoção.'),
(97, '022.2', '', ' MOVIMENTAÇÃO DE PESSOAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos procedimentos que efetivam as ações de lotação, exercício, permuta, cessão e requisição.'),
(98, '022.21', '', ' LOTAÇÃO, EXERCÍCIO E PERMUTA', '', '', '', ' Incluem-se documentos referentes aos procedimentos que efetivam as ações de lotação, exercício e permuta, bem como aqueles referentes à transferência.'),
(99, '022.22', '', ' CESSÃO. REQUISIÇÃO', '', '', '', ' Incluem-se documentos referentes aos procedimentos que efetivam as ações de cessão e requisição para a realização de serviços temporários em outro órgão e entidade.'),
(100, '022.3', '', ' REMOÇÃO', '', '', '', ' Incluem-se documentos referentes aos procedimentos que efetivam as ações de remoção.'),
(101, '022.4', '', ' REDISTRIBUIÇÃO', '', '', '', ' Incluem-se documentos referentes aos procedimentos que efetivam as ações de redistribuição.'),
(102, '022.5', '', ' SUBSTITUIÇÃO', '', '', '', ' Incluem-se documentos referentes aos procedimentos que efetivam as ações de substituição.'),
(103, '022.6', '', ' AVALIAÇÃO DE DESEMPENHO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao cumprimento do estágio obrigatório pelo servidor público, à homologação de sua estabilidade e ao período de experiência a ser cumprido pelos contratados, bem como aqueles referentes às promoções e progressões funcionais.'),
(104, '022.61', '', ' CUMPRIMENTO DE ESTÁGIO PROBATÓRIO. HOMOLOGAÇÃO DA ESTABILIDADE', '', '', '', ' Incluem-se documentos referentes ao cumprimento e à avaliação do estágio probatório e à homologação da estabilidade do servidor público.'),
(105, '022.62', '', ' CUMPRIMENTO DO PERÍODO DE EXPERIÊNCIA', '', '', '', ' Incluem-se documentos referentes ao período de experiência a ser cumprido pelos contratados.'),
(106, '022.63', '', ' PROMOÇÃO E PROGRESSÃO FUNCIONAL', '', '', '', ' Incluem-se documentos referentes às avaliações de desempenho para promoção e progressão funcional dos servidores. Quanto à reestruturação e alteração salarial decorrentes de promoção e progressão funcional, classificar no código 023.12.'),
(107, '022.7', '', ' VACÂNCIA', '', '', '', ' Incluem-se documentos referentes aos procedimentos que efetivam as ações de demissão, dispensa, exoneração, rescisão contratual, aviso prévio, posse em outro cargo não acumulável, promoção, readaptação, aposentadoria e falecimento, bem como aqueles referentes à adesão aos planos de demissão voluntária.'),
(108, '023', '', ' CONCESSÃO DE DIREITOS E VANTAGENS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à percepção de pagamento de vencimentos, remunerações, salários e proventos e ao gozo de férias, licenças, afastamentos, concessões, auxílios e reembolso de despesas, bem como aqueles referentes aos descontos, obrigações trabalhistas e estatutárias, encargos patronais e recolhimentos.'),
(109, '023.1', '', ' PAGAMENTO DE VENCIMENTOS. REMUNERAÇÕES. SALÁRIOS. PROVENTOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos procedimentos que efetivam as ações de percepção de pagamento.'),
(110, '023.11', '', ' FOLHAS DE PAGAMENTO', '', '', '', ' Incluem-se folhas de pagamento, fichas financeiras e relação de pagamentos.'),
(111, '023.12', '', ' REESTRUTURAÇÃO E ALTERAÇÃO SALARIAL', '', '', '', ' Incluem-se documentos referentes a reestruturações e alterações salariais decorrentes de promoção e progressão funcional, enquadramento, equiparação, reajuste e reposição salarial, bem como aqueles decorrentes da redução de jornada de trabalho, remuneração proporcional, regime de trabalho integral e dedicação exclusiva. Quanto à criação, transformação e reestruturação de cargos, carreiras e remuneração, classificar no código 020.022. Quanto às avaliações de desempenho para promoção e progressão funcional, classificar no código 022.63.'),
(112, '023.13', '', ' ABONO PROVISÓRIO', '', '', '', ' Incluem-se documentos referentes à comprovação do direito, à solicitação e ao pagamento de acréscimo financeiro provisório na remuneração.'),
(113, '023.14', '', ' ABONO DE PERMANÊNCIA EM SERVIÇO', '', '', '', ' Incluem-se documentos referentes ao reembolso da contribuição previdenciária ao servidor público, que cumpriu os requisitos para aposentadoria, mas que optou por continuar na ativa. Quanto à contagem e averbação de tempo de serviço, classificar no código 026.02.'),
(114, '023.15', '', ' GRATIFICAÇÕES', '', '', '', ' Nas subdivisões deste descritor classificam-se os documentos referentes à solicitação, comprovação do direito, incorporação, pagamento e interrupção do pagamento das gratificações concedidas.'),
(115, '023.151', '', ' FUNÇÃO', '', '', '', ' Incluem-se documentos referentes à solicitação, comprovação do direito, incorporação de quintos e décimos, pagamento e interrupção do pagamento da gratificação.'),
(116, '023.152', '', ' JETONS', '', '', '', ' Incluem-se documentos referentes à solicitação, comprovação do direito, incorporação, pagamento e interrupção do pagamento da gratificação.'),
(117, '023.153', '', ' CARGOS EM COMISSÃO', '', '', '', ' Incluem-se documentos referentes à solicitação, comprovação do direito, incorporação, pagamento e interrupção do pagamento da gratificação.'),
(118, '023.154', '', ' NATALINA. DÉCIMO TERCEIRO SALÁRIO', '', '', '', ' Incluem-se documentos referentes ao pagamento e adiantamento da gratificação.'),
(119, '023.155', '', ' DESEMPENHO', '', '', '', ' Incluem-se documentos referentes às gratificações por desempenho de atividade, qualificação e produtividade, bem como aqueles referentes à solicitação de inclusão e ao cancelamento do pagamento da gratificação.'),
(120, '023.156', '', ' ENCARGO DE CURSO E CONCURSO', '', '', '', ' Incluem-se documentos referentes à solicitação e pagamento da gratificação por encargo de curso ministrado, bem como aqueles referentes à participação em bancas examinadoras e de fiscalização e aplicação de provas em concursos.'),
(121, '023.157', '', ' TITULAÇÃO', '', '', '', ' Incluem-se documentos referentes à solicitação, comprovação do direito, incorporação e pagamento da retribuição por titulação, obtida pela conclusão de cursos de especialização, mestrado e doutorado.'),
(122, '023.16', '', ' ADICIONAIS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à solicitação, comprovação do direito, pagamento e interrupção do pagamento dos adicionais concedidos.'),
(123, '023.161', '', ' TEMPO DE SERVIÇO', '', '', '', ' Incluem-se documentos referentes ao acréscimo financeiro sobre a remuneração em razão do cumprimento de cada ano de serviço público efetivo, tais como: anuênios, biênios e quinquênios.'),
(124, '023.162', '', ' NOTURNO', '', '', '', ' Incluem-se documentos referentes ao acréscimo financeiro na remuneração em razão de trabalho noturno.'),
(125, '023.163', '', ' PERICULOSIDADE', '', '', '', ' Incluem-se documentos referentes ao acréscimo financeiro na remuneração em razão do trabalho ser executado em condições perigosas.'),
(126, '023.164', '', ' INSALUBRIDADE', '', '', '', ' Incluem-se documentos referentes ao acréscimo financeiro na remuneração em razão do trabalho ser executado em ambiente exposto a agentes físicos, químicos ou biológicos que sejam nocivos à saúde.'),
(127, '023.165', '', ' ATIVIDADE PENOSA', '', '', '', ' Incluem-se documentos referentes ao acréscimo financeiro na remuneração em razão do trabalho debilitar gradativamente a saúde física e mental.'),
(128, '023.166', '', ' SERVIÇO EXTRAORDINÁRIO. HORAS EXTRAS', '', '', '', ' Incluem-se documentos referentes ao acréscimo financeiro na remuneração em razão do trabalho a ser executado além do número de horas estipuladas na jornada de trabalho.'),
(129, '023.167', '', ' UM TERÇO DE FÉRIAS. ABONO PECUNIÁRIO', '', '', '', ' Incluem-se documentos referentes ao acréscimo financeiro na remuneração de um terço do seu valor no mês de férias, bem como aqueles referentes à conversão de um terço dos dias de férias em pecúnia. Quanto ao afastamento para gozo de férias, classificar no código 023.2.'),
(130, '023.17', '', ' DESCONTOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos descontos que incidem na remuneração do servidor.'),
(131, '023.171', '', ' CONTRIBUIÇÃO SINDICAL, ASSISTENCIAL E CONFEDERATIVA', '', '', '', ' Incluem-se documentos referentes à autorização para desconto incidente sobre a remuneração em razão de contribuição sindical, confederativa e retributiva, taxa assistencial e mensalidade sindical, bem como aqueles referentes à solicitação de cancelamento do desconto. Quanto à relação com sindicatos, classificar no código 020.031. Quanto à contribuição sindical do empregador, classificar no código 023.183.'),
(132, '023.172', '', ' CONTRIBUIÇÃO PARA O PLANO DE SEGURIDADE SOCIAL', '', '', '', ' Incluem-se documentos referentes ao desconto incidente sobre a remuneração em razão da contribuição para o plano de seguridade social, bem como aqueles referentes ao repasse do valor ao órgão competente. Quanto à contribuição para o plano de seguridade social recolhida pelo empregador, classificar no código 023.184.'),
(133, '023.173', '', ' IMPOSTO DE RENDA RETIDO NA FONTE (IRRF)', '', '', '', ' Incluem-se documentos referentes ao desconto do imposto de renda retido na fonte, bem como aqueles referentes à solicitação de isenção de pagamento, por parte do servidor portador de doença específica ou aposentado por invalidez permanente. Quanto ao imposto de renda recolhido pela fonte pagadora, classificar no código 023.185.'),
(134, '023.174', '', ' PENSÃO ALIMENTÍCIA', '', '', '', ' Incluem-se documentos referentes à autorização para desconto incidente sobre a remuneração em razão do pagamento de pensão alimentícia, bem como aqueles referentes à solicitação de cancelamento do desconto.'),
(135, '023.175', '', ' CONSIGNAÇÕES FACULTATIVAS', '', '', '', ' Incluem-se documentos referentes à autorização para desconto incidente sobre a remuneração em razão de contribuição e coparticipação para plano de saúde, de contribuição e coparticipação para entidade de previdência complementar, de prêmio relativo a seguro de vida, de contribuição em favor de associação ou cooperativa, de empréstimo, de financiamento imobiliário e de despesa contraída e saque realizado por meio de cartão de crédito, bem como aqueles referentes à solicitação de cancelamento do desconto e devolução de descontos indevidos.'),
(136, '023.18', '', ' OBRIGAÇÕES TRABALHISTAS E ESTATUTÁRIAS, ENCARGOS PATRONAIS E RECOLHIMENTOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos encargos patronais e recolhimentos efetuados pelo empregador.'),
(137, '023.181', '', ' PROGRAMA DE FORMAÇÃO DO PATRIMÔNIO DO SERVIDOR PÚBLICO (PASEP). PROGRAMA DE INTEGRAÇÃO SOCIAL (PIS)', '', '', '', ' Incluem-se documentos referentes às contribuições sociais, de natureza tributária, devidas pelo órgão e entidade, com o objetivo de financiar o pagamento do segurodesemprego, do abono e da participação na receita do órgão e entidade e que são destinados aos servidores.'),
(138, '023.182', '', ' FUNDO DE GARANTIA DO TEMPO DE SERVIÇO (FGTS)', '', '', '', ' Incluem-se documentos referentes à declaração de opção e à comprovação do depósito do valor do FGTS.'),
(139, '023.183', '', ' CONTRIBUIÇÃO SINDICAL DO EMPREGADOR', '', '', '', ' Incluem-se documentos referentes ao pagamento da contribuição sindical feita pelo empregador. Quanto à relação com sindicatos, classificar no código 020.031. Quanto à contribuição sindical, assistencial e confederativa do servidor, classificar no código 023.171.'),
(140, '023.184', '', ' CONTRIBUIÇÃO PARA O PLANO DE SEGURIDADE SOCIAL', '', '', '', ' Incluem-se documentos referentes à contribuição social recolhida pelo empregador para o regime previdenciário correspondente. Quanto à contribuição para o plano de seguridade social descontada da remuneração do servidor, classificar no código 023.172.'),
(141, '023.185', '', ' IMPOSTO DE RENDA', '', '', '', ' Incluem-se documentos referentes ao recolhimento, de natureza tributária, do valor retido da renda do beneficiário, que é efetuado pela fonte pagadora. Quanto ao imposto de renda descontado da remuneração do servidor, classificar no código 023.173.'),
(142, '023.186', '', ' LEI DOS DOIS TERÇOS. RELAÇÃO ANUAL DE INFORMAÇÕES SOCIAIS (RAIS)', '', '', '', ' Incluem-se documentos referentes ao cumprimento da lei dos 2/3 e às declarações de Rais, que subsidiam o controle das atividades trabalhistas no órgão e entidade.'),
(143, '023.19', '', ' OUTRAS AÇÕES REFERENTES AO PAGAMENTO DE VENCIMENTOS. REMUNERAÇÕES. SALÁRIOS. PROVENTOS', '', '', '', ' Na subdivisão deste descritor classificam-se documentos referentes a outras ações de pagamento de vencimentos, remunerações, salários e proventos não contempladas nos descritores anteriores.'),
(144, '023.191', '', ' RETIFICAÇÃO DE PAGAMENTO', '', '', '', ' Incluem-se documentos referentes aos pedidos, feitos pelo servidor, para a retificação de erros efetuados no pagamento. Quanto aos casos de restituição de valores ao erário, classificar no código 059.4.'),
(145, '023.2', '', ' FÉRIAS', '', '', '', ' Incluem-se documentos referentes à concessão de férias, tais como: programação, alteração, cancelamento, suspensão, escala e aviso de férias. Quanto ao pagamento de adicional de um terço de férias e abono pecuniário, classificar no código 023.167.'),
(146, '023.3', '', ' LICENÇAS', '', '', '', ' Incluem-se documentos referentes à concessão e à prorrogação de licenças para afastamento do cônjuge ou companheiro, atividade política, capacitação profissional, participação em programa de pósgraduação lato sensu, para desempenho de mandato classista, motivo de doença em pessoa da família, incentivada, sem remuneração, prêmio por assiduidade, serviço militar e tratamento de interesses particulares, bem como as perícias médicas realizadas para concessão e prorrogação das licenças, quando forem necessárias. Quanto às licenças referentes à concessão de benefícios de seguridade e previdência social (acidente em serviço, tratamento de saúde, gestante, paternidade e adotante), classificar no código 026.4.'),
(147, '023.4', '', ' AFASTAMENTOS', '', '', '', ' Incluem-se documentos referentes à suspensão de contrato de trabalho e concessão de afastamento para depor, exercer mandato eletivo, servir a Justiça Eleitoral, servir como jurado, participar em programas de pósgraduação stricto sensu, de pósdoutorado e em estudos, no país e no exterior. Quanto ao afastamento para o cumprimento de missões e viagens a serviço, no país e no exterior, e para servir em organismo internacional de que o Brasil participe ou com o qual coopere, classificar nas subdivisões do código 028. Quanto à participação em programa de pósgraduação lato sensu, classificar no código 023.3, no âmbito da capacitação profissional.');
INSERT INTO `ttddnew1` (`id`, `symbol`, `kind`, `assunto`, `fase_corrente`, `fase_intermediaria`, `destinacao`, `observacoes`) VALUES
(148, '023.5', '', ' CONCESSÕES', '', '', '', ' Incluem-se documentos referentes à comprovação de ausência no serviço em razão de alistamento eleitoral, casamento (gala), doação de sangue e falecimento de familiares (nojo), bem como aqueles referentes à concessão de horário especial para servidor estudante, servidor portador de deficiência e servidor que possua dependente portador de deficiência, com ou sem compensação de horas. Quanto à concessão de benefícios de seguridade e previdência social, classificar nas subdivisões do código 026.'),
(149, '023.6', '', ' AUXÍLIOS', '', '', '', ' Incluem-se documentos referentes à solicitação, comprovação do direito, pagamento e interrupção do pagamento dos auxílios alimentação ou refeição, assistência préescolar ou creche, moradia e valetransporte, bem como o auxíliomoradia para liquidante. Quanto aos auxílios referentes à concessão de benefícios de seguridade e previdência social (acidente, doença, funeral e natalidade), classificar no código 026.3. Quanto ao auxílioreclusão, classificar no código 026.91.'),
(150, '023.7', '', ' REEMBOLSO DE DESPESAS. INDENIZAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao reembolso de despesas efetuadas em razão de mudança de domicílio, locomoção com meio de transporte e custeio de assistência suplementar à saúde.'),
(151, '023.71', '', ' MUDANÇA DE DOMICÍLIO', '', '', '', ' Incluem-se documentos referentes às ajudas de custo para compensação e reembolso das despesas de instalação do servidor e de sua família que, no interesse do órgão e entidade, passar a ter exercício em nova sede.'),
(152, '023.72', '', ' LOCOMOÇÃO', '', '', '', ' Incluem-se documentos referentes ao reembolso de despesas efetuadas com a utilização de meio de locomoção, próprio ou não, para a execução de serviços externos. Quanto ao fornecimento de transporte, classificar no código 023.93. Quanto à prestação de serviço de transporte remunerado de passageiro(s) por demanda, classificar no código 018.1.'),
(153, '023.73', '', ' RESSARCIMENTO DE PLANO DE SAÚDE', '', '', '', ' Incluem-se documentos relativos à participação do órgão e entidade no custeio da assistência suplementar à saúde do servidor e demais beneficiários.'),
(154, '023.9', '', ' OUTRAS AÇÕES REFERENTES À CONCESSÃO DE DIREITOS E VANTAGENS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes a outras ações de concessão de direitos e vantagens não contempladas nos descritores anteriores.'),
(155, '023.91', '', ' CONTRATAÇÃO DE SEGURO', '', '', '', ' Incluem-se documentos referentes à contratação de seguro de vida em grupo e seguro de acidentes pessoais. Quanto à contratação de seguro patrimonial, classificar no código 018.1.'),
(156, '023.92', '', ' OCUPAÇÃO DE IMÓVEL FUNCIONAL', '', '', '', ' Incluem-se documentos referentes à ocupação de imóvel funcional, tais como: solicitação, termo de ocupação e de responsabilidade.'),
(157, '023.93', '', ' FORNECIMENTO DE TRANSPORTE', '', '', '', ' Incluem-se documentos referentes à comprovação do fornecimento de serviço de transporte, oferecido pelo órgão e entidade, para que o servidor se desloque de sua residência para o local de trabalho e viceversa. Quanto ao reembolso de despesas para locomoção, classificar no código 023.72. Quanto à prestação de serviço de transporte remunerado de passageiro(s) por demanda, classificar no código 018.1.'),
(158, '024', '', ' CAPACITAÇÃO DO SERVIDOR', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à promoção, elaboração e execução de programas de capacitação, desenvolvimento e valorização do servidor. Quanto ao pagamento de cursos para servidores, classificar no código 052.221.'),
(159, '024.01', '', ' PLANEJAMENTO DA CAPACITAÇÃO', '', '', '', ' Incluem-se documentos referentes ao mapeamento das competências institucionais e individuais, diagnóstico das competências profissionais e o plano anual de capacitação proporcionado pelo órgão e entidade ao servidor.'),
(160, '024.1', '', ' PROMOÇÃO DE CURSOS PELO ÓRGÃO E ENTIDADE', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à promoção, pelo órgão e entidade, de cursos de capacitação destinados ao servidor. Quanto aos documentos comprobatórios de participação do servidor, que deverão integrar o assentamento funcional, classificar nas subdivisões do código 020.1.'),
(161, '024.11', '', ' PROGRAMAÇÃO', '', '', '', ' Incluem-se documentos referentes à divulgação do curso e à definição do conteúdo programático, bem como exemplares únicos de exercícios e apostilas.'),
(162, '024.12', '', ' INSCRIÇÃO E FREQUÊNCIA', '', '', '', ' Incluem-se documentos referentes aos procedimentos para inscrição, lista de participantes, controle de entrega de material e lista de frequência dos participantes.'),
(163, '024.13', '', ' AVALIAÇÃO E RESULTADOS', '', '', '', ' Incluem-se documentos referentes aos resultados alcançados, avaliação do curso pelos participantes, controle de expedição e entrega de certificados e relatórios.'),
(164, '024.2', '', ' PARTICIPAÇÃO EM CURSOS PROMOVIDOS POR OUTROS ÓRGÃOS E ENTIDADES', '', '', '', ' Incluem-se documentos referentes à divulgação do curso, programa, termo de compromisso e relatório de participação do servidor no curso. Quanto aos documentos comprobatórios de participação do servidor, que deverão integrar o assentamento funcional, classificar nas subdivisões do código 020.1.'),
(165, '024.3', '', ' PROMOÇÃO DE ESTÁGIOS PELO ÓRGÃO E ENTIDADE', '', '', '', ' Nas subdivisões deste descritor, classificam-se documentos referentes a promoção, pelo órgão e entidade, de estágios destinados ao servidor. Quanto aos documentos comprobatórios de participação do servidor, que deverão integrar o assentamento funcional, classificar nas subdivisões do código 020.1.'),
(166, '024.31', '', ' PROGRAMAÇÃO', '', '', '', ' Incluem-se documentos referentes à divulgação do estágio e à definição do conteúdo programático.'),
(167, '024.32', '', ' INSCRIÇÃO E FREQUÊNCIA', '', '', '', ' Incluem-se documentos referentes aos procedimentos para inscrição, controle de entrega de material e lista de frequência dos participantes, bem como aqueles referentes à concessão de bolsas de estágio.'),
(168, '024.33', '', ' AVALIAÇÃO E RESULTADOS', '', '', '', ' Incluem-se documentos referentes aos resultados alcançados, avaliação do estágio pelos participantes, controle de expedição e entrega de declaração de participação e relatórios.'),
(169, '024.4', '', ' PARTICIPAÇÃO EM ESTÁGIOS PROMOVIDOS POR OUTROS ÓRGÃOS E ENTIDADES', '', '', '', ' Incluem-se documentos referentes à divulgação do estágio, programa, termo de compromisso e relatório de participação do servidor no estágio. Quanto aos documentos comprobatórios de participação do servidor, que deverão integrar o assentamento funcional, classificar nas subdivisões do código 020.1.'),
(170, '024.5', '', ' CONCESSÃO DE ESTÁGIOS E BOLSAS PARA ESTUDANTES', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à oferta de estágios, remunerados ou não, e à concessão de bolsas para estudantes (residentes médicos, multiprofissionais e estagiários), por parte do órgão e entidade.'),
(171, '024.51', '', ' RELAÇÃO COM INSTITUIÇÕES DE ENSINO E AGENTES DE INTEGRAÇÃO', '', '', '', ' Incluem-se documentos referentes à celebração de convênios com instituições de ensino superior e outras que visem à oferta de estágios, à integração da empresaescola e à concessão de bolsas para estudantes.'),
(172, '024.52', '', ' PLANO DE ESTÁGIO', '', '', '', ' Incluem-se documentos referentes ao estabelecimento das atividades a serem realizadas pelos estudantes.'),
(173, '025', '', ' PROMOÇÃO DA SAÚDE E BEMESTAR', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às atividades de promoção da saúde e bemestar do servidor.'),
(174, '025.1', '', ' ASSISTÊNCIA À SAÚDE', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à celebração de convênios e ao desenvolvimento de ações voltadas para a saúde do servidor, bem como o registro nos prontuários de pacientes sobre a assistência prestada.'),
(175, '025.11', '', ' CELEBRAÇÃO DE CONVÊNIOS DE ASSISTÊNCIA À SAÚDE', '', '', '', ' Incluem-se documentos referentes à celebração de convênios firmados para a prestação de assistência à saúde destinados ao servidor e seus dependentes.'),
(176, '025.12', '', ' ORIENTAÇÃO PARA CUIDADOS COM A SAÚDE', '', '', '', ' Incluem-se documentos referentes às ações de orientação, acompanhamento e execução de iniciativas de cuidado com a saúde em áreas multidisciplinares, como medicina, nutrição, odontologia e psicologia, que visem o bemestar do servidor.'),
(177, '025.13', '', ' PROMOÇÃO DE ATIVIDADE FÍSICA', '', '', '', ' Incluem-se documentos referentes às ações de promoção e execução de iniciativas que visem a prática da ginástica laboral e incentivem o servidor à prática de atividades físicas.'),
(178, '025.14', '', ' REGISTRO DE ASSISTÊNCIA À SAÚDE EM PRONTUÁRIO', '', '', '', ' Incluem-se documentos referentes aos registros de assistência à saúde em prontuário de paciente, prestada aos servidores, tais como pareceres, laudos, exames e inspeções periódicas de saúde, exames complementares e comunicação de alta e óbito. Quanto aos exames admissionais e demissionais do servidor, que deverão integrar o assentamento funcional, classificar nas subdivisões do código 020.1.'),
(179, '025.2', '', ' PRESERVAÇÃO DA SAÚDE E HIGIENE', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às iniciativas do órgão e entidade que visam à preservação da saúde e a garantia de condições satisfatórias, individuais e ambientais, nos locais de trabalho.'),
(180, '025.21', '', ' CONTROLE DE RISCOS AMBIENTAIS', '', '', '', ' Incluem-se documentos referentes ao perfil profissiográfico e ao levantamento, avaliação e controle da ocorrência de riscos ambientais (agentes químicos, físicos e biológicos) existentes nos locais de trabalho, bem como aqueles referentes aos programas de controle médico de saúde ocupacional e de prevenção de riscos ambientais e os laudos e certificados sobre inspeções sanitárias e equipamentos de proteção individual. Quanto à proteção ambiental interna, classificar no código 017.1.'),
(181, '025.22', '', ' OFERTA DE SERVIÇOS DE REFEITÓRIOS, CANTINAS E COPAS', '', '', '', ' Incluem-se documentos referentes às regras gerais de uso e de funcionamento dos ambientes destinados às refeições.'),
(182, '025.3', '', ' SEGURANÇA DO TRABALHO. PREVENÇÃO DE ACIDENTES DE TRABALHO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às medidas preventivas relacionadas ao ambiente de trabalho, visando à redução de acidentes e doenças ocupacionais. Quanto ao pagamento dos adicionais de periculosidade, insalubridade e atividade penosa, classificar nos códigos 023.163, 023.164 e 023.165, respectivamente.'),
(183, '025.31', '', ' CONSTITUIÇÃO DA COMISSÃO INTERNA DE PREVENÇÃO DE ACIDENTES (Cipa)', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos procedimentos para a constituição e atuação da comissão.'),
(184, '025.311', '', ' COMPOSIÇÃO E ATUAÇÃO', '', '', '', ' Incluem-se documentos referentes à constituição da Cipa, editais de convocação e divulgação das eleições, constituição da comissão eleitoral, folha de votação e atas da eleição (nos casos de realização de processo eleitoral) ou indicação e designação dos membros e instalação e posse da comissão (em outras situações que não envolvam processo eleitoral), bem como aqueles referentes aos estudos e às inspeções relativas à qualidade e segurança do ambiente de trabalho, mapas de riscos, laudos e pareceres técnicos, atas, relatórios e campanhas de divulgação.'),
(185, '025.312', '', ' OPERACIONALIZAÇÃO DE REUNIÕES', '', '', '', ' Incluem-se documentos referentes à organização das reuniões da Cipa, bem como aqueles referentes ao agendamento, convocação, pauta e lista de participantes.'),
(186, '025.32', '', ' REGISTRO DE OCORRÊNCIAS DE ACIDENTES DE TRABALHO', '', '', '', ' Incluem-se os comunicados e os registros das ocorrências e as sindicâncias instaladas para averiguação dos acidentes de trabalho.'),
(187, '026', '', ' CONCESSÃO DE BENEFÍCIOS DE SEGURIDADE E PREVIDÊNCIA SOCIAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à concessão de benefícios previdenciários e assistenciais ao servidor. Quanto às concessões referentes aos direitos e vantagens (ausência no serviço em razão de alistamento eleitoral, casamento, doação de sangue, falecimento de familiares, horário especial para servidor estudante, servidor portador de deficiência e servidor que possua dependente portador de deficiência), classificar no código 023.5.'),
(188, '026.01', '', ' ADESÃO À PREVIDÊNCIA COMPLEMENTAR', '', '', '', ' Incluem-se documentos referentes à contribuição aos planos privados de pecúlios, rendas e benefícios complementares ou assemelhados aos da previdência social.'),
(189, '026.02', '', ' CONTAGEM E AVERBAÇÃO DE TEMPO DE SERVIÇO', '', '', '', ' Incluem-se documentos referentes à comprovação do tempo de contribuição previdenciária e à solicitação de averbação do tempo de serviço correspondente. Quanto ao abono de permanência em serviço, classificar no código 023.14.'),
(190, '026.1', '', ' SALÁRIO FAMÍLIA', '', '', '', ' Incluem-se documentos referentes à solicitação, comprovação do direito, pagamento e interrupção do pagamento do benefício.'),
(191, '026.2', '', ' SALÁRIO MATERNIDADE', '', '', '', ' Incluem-se documentos referentes à solicitação, comprovação do direito, pagamento e interrupção do pagamento do benefício.'),
(192, '026.3', '', ' AUXÍLIOS', '', '', '', ' Incluem-se documentos referentes à solicitação, comprovação do direito, pagamento e interrupção do pagamento dos auxílios acidente, doença, funeral e natalidade. Quanto aos auxílios referentes à concessão de direitos e vantagens (alimentação ou refeição, assistência préescolar ou creche, moradia, valetransporte e moradia de liquidante), classificar no código 023.6. Quanto ao auxílioreclusão, classificar no código 026.91.'),
(193, '026.4', '', ' LICENÇAS', '', '', '', ' Incluem-se documentos referentes à concessão de licenças para acidente em serviço, tratamento de saúde, gestante, paternidade e adotante, bem como as perícias médicas realizadas para concessão e prorrogação das licenças, quando forem necessárias. Quanto às licenças referentes à concessão de direitos e vantagens (afastamento do cônjuge ou companheiro, atividade política, capacitação profissional, desempenho de mandato classista, motivo de doença em pessoa da família, incentivada sem remuneração, prêmio por assiduidade, serviço militar e tratamento de interesses particulares), classificar no código 023.3.'),
(194, '026.5', '', ' APOSENTADORIA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à solicitação e concessão de aposentadoria por invalidez permanente, por idade, por tempo de contribuição previdenciária e aposentadoria especial.'),
(195, '026.51', '', ' INVALIDEZ PERMANENTE', '', '', '', ' Incluem-se documentos referentes à solicitação e concessão de aposentadoria por invalidez permanente em decorrência de acidente em serviço, moléstia profissional e doença grave, contagiosa ou incurável, especificadas em legislação.'),
(196, '026.52', '', ' COMPULSÓRIA', '', '', '', ' Incluem-se documentos referentes à concessão de aposentadoria compulsória, de acordo com a legislação em vigor, com proventos proporcionais ao tempo de contribuição previdenciária.'),
(197, '026.53', '', ' VOLUNTÁRIA', '', '', '', ' Incluem-se documentos referentes à solicitação e concessão de aposentadoria voluntária, atendendo aos requisitos de tempo de contribuição previdenciária e de idade mínima.'),
(198, '026.54', '', ' ESPECIAL', '', '', '', ' Incluem-se documentos referentes à solicitação e concessão de aposentadoria ao servidor que trabalhou exposto a agentes nocivos à saúde, como calor ou ruído, de forma contínua e ininterrupta, em níveis de exposição acima dos limites, conforme especificado em legislação.'),
(199, '026.6', '', ' PENSÃO POR MORTE', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à solicitação, comprovação do direito, habilitação, incorporação, pagamento e interrupção do pagamento das pensões concedidas por morte do servidor.'),
(200, '026.61', '', ' PENSÃO PROVISÓRIA. PENSÃO TEMPORÁRIA', '', '', '', ' Incluem-se documentos referentes à solicitação, comprovação do direito, habilitação, pagamento e interrupção do pagamento da pensão, bem como aqueles referentes à avaliação periódica das condições que ensejaram a concessão do benefício.'),
(201, '026.62', '', ' PENSÃO VITALÍCIA', '', '', '', ' Incluem-se documentos referentes à solicitação, comprovação do direito, habilitação e pagamento da pensão.'),
(202, '026.9', '', ' OUTRAS AÇÕES REFERENTES À CONCESSÃO DE BENEFÍCIOS DE SEGURIDADE E PREVIDÊNCIA SOCIAL', '', '', '', ' Na subdivisão deste descritor classificam-se documentos referentes a outras ações de concessão de benefícios de seguridade e previdência social não contempladas nos descritores anteriores.'),
(203, '026.91', '', ' AUXÍLIO RECLUSÃO', '', '', '', ' Incluem-se documentos referentes à solicitação, comprovação do direito, pagamento e interrupção do pagamento do auxílioreclusão aos dependentes do servidor. Quanto aos auxílios alimentação ou refeição, assistência préescolar ou creche, moradia, valetransporte e moradia de liquidante, classificar no código 023.6. Quanto aos auxílios acidente, doença, funeral e natalidade, classificar no código 026.3.'),
(204, '027', '', ' APURAÇÃO DE RESPONSABILIDADE DISCIPLINAR', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às apurações de responsabilidade que envolvem o servidor. Quanto ao extravio, roubo, desaparecimento, furto e avaria de material, classificar no código 033.6.'),
(205, '027.1', '', ' AVERIGUAÇÃO DE DENÚNCIAS', '', '', '', ' Incluem-se documentos referentes aos procedimentos disciplinares para a apuração de denúncias sobre possíveis infrações ou irregularidades praticadas pelos servidores no exercício de suas atribuições, bem como aqueles produzidos em decorrência da instauração de inquéritos, sindicâncias e processo administrativo disciplinar (PAD).'),
(206, '027.2', '', ' APLICAÇÃO DE PENALIDADES DISCIPLINARES', '', '', '', ' Incluem-se documentos referentes à imposição de penalidades em razão da conclusão da apuração de responsabilidade disciplinar, podendo se constituir em uma advertência, suspensão, demissão, cassação de aposentadoria, disponibilidade, destituição de cargo em comissão e destituição de função comissionada. Quanto ao registro das penalidades disciplinares aplicadas ao servidor, que deverão integrar o assentamento funcional, classificar nas subdivisões do código 020.1.'),
(207, '027.3', '', ' AJUSTAMENTO DE CONDUTA', '', '', '', ' Incluem-se documentos referentes à celebração de termo de ajustamento de conduta (TAC) nos casos de infração disciplinar de menor potencial ofensivo e de desvios de conduta de baixa lesividade praticados pelo servidor.'),
(208, '028', '', ' CUMPRIMENTO DE MISSÕES E VIAGENS A SERVIÇO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao afastamento do servidor para o cumprimento de missões e a realização de viagens a serviço, no país e no exterior, para colaboração ou participação esporádica em assuntos que é especialista, e para servir em organismo internacional de que o Brasil participe ou com o qual coopere. Quanto ao planejamento, acompanhamento, execução, avaliação e relatório técnico, classificar no código específico relativo ao objeto da missão e da viagem a serviço. Quanto aos afastamentos referentes à concessão de direitos e vantagens (suspensão de contrato de trabalho, depor, exercer mandato legislativo, servir a Justiça Eleitoral, servir como jurado, participar em programas de pósgraduação stricto sensu e de pósdoutorado e em estudos, no país e no exterior), classificar no código 023.4.'),
(209, '028.1', '', ' NO PAÍS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao afastamento do servidor para o cumprimento de missões e a realização de viagens no país, podendo ser: com ônus (quando implicarem no direito à concessão de passagens e diárias e ao recebimento de vencimentos ou salários e demais vantagens) e com ônus limitado (quando implicarem, apenas, no direito ao recebimento de vencimentos e salários e demais vantagens).'),
(210, '028.11', '', ' COM ÔNUS', '', '', '', ' Incluem-se documentos referentes à solicitação e autorização de afastamento, ajudas de custo, diárias, passagens, reservas de hotel, pagamento de despesas de eventos, prestações de contas e relatórios de viagem, bem como lista de participantes (no caso de comitivas e delegações) e concessão de passagens e diárias para convidados.'),
(211, '028.12', '', ' COM ÔNUS LIMITADO', '', '', '', ' Incluem-se documentos referentes à solicitação e autorização de afastamento para participar de eventos ou cumprir missões e realizar viagens no país, bem como aqueles referentes à solicitação de afastamento para colaboração ou participação esporádica, (remunerada ou não), em assuntos que é especialista.'),
(212, '028.2', '', ' NO EXTERIOR', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao afastamento do país do servidor para o cumprimento de missões e a realização de viagens ao exterior, podendo ser: com ônus (quando implicarem no direito à concessão de passagens e diárias e ao recebimento de vencimentos ou salários e demais vantagens), com ônus limitado (quando implicarem, apenas, no direito ao recebimento de vencimentos e salários e demais vantagens) e sem ônus (quando implicarem a perda total dos vencimentos ou salários e demais vantagens, não acarretando qualquer despesa para o órgão e entidade).'),
(213, '028.21', '', ' COM ÔNUS', '', '', '', ' Incluem-se documentos referentes à solicitação e autorização de afastamento do país, ajudas de custo, diárias, compra de moeda estrangeira, passagens, reservas de hotel, pagamento de despesas de eventos, prestações de contas e relatórios de viagem, bem como lista de participantes (no caso de comitivas e delegações) e concessão de passagens e diárias para convidados.'),
(214, '028.22', '', ' COM ÔNUS LIMITADO', '', '', '', ' Incluem-se documentos referentes à solicitação e autorização de afastamento do país para participar de eventos ou cumprir missões e realizar viagens ao exterior.'),
(215, '028.23', '', ' SEM ÔNUS', '', '', '', ' Incluem-se documentos referentes à solicitação e autorização de afastamento do país para participar de eventos ou cumprir missões e realizar viagens ao exterior.'),
(216, '029', '', ' OUTRAS AÇÕES REFERENTES À GESTÃO DE PESSOAS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes a outras ações de gestão de pessoas não contempladas nos descritores anteriores.'),
(217, '029.1', '', ' CONTROLE DE ASSIDUIDADE E PONTUALIDADE', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao controle de frequência dos servidores, ao estabelecimento do horário de expediente do órgão e entidade e ao controle do cumprimento da jornada de trabalho regular fora das dependências do órgão ou entidade.'),
(218, '029.11', '', ' CONTROLE DE FREQUÊNCIA', '', '', '', ' Incluem-se livros, cartões e folhas de ponto, bem como documentos referentes ao abono de faltas, cumprimento de horário especial e de horas extras, banco de horas, corte de ponto e registro de dias parados.'),
(219, '029.12', '', ' DEFINIÇÃO DO HORÁRIO DE EXPEDIENTE', '', '', '', ' Incluem-se documentos referentes à fixação e mudanças do horário de funcionamento do órgão e entidade e às escalas de plantão.'),
(220, '029.13', '', ' REGISTRO DAS ATIVIDADES DE TELETRABALHO', '', '', '', ' Incluem-se documentos referentes a definição ou a prestação de contas das atividades realizadas remotamente, em substituição ao controle de frequência, tais como: plano de trabalho, tabela de atividades, relatório ou outra forma de prestação de contas das atividades e/ou entregas realizadas.'),
(221, '029.2', '', ' INSTITUIÇÃO DO PROGRAMA DE CRECHE', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à implantação do programa de creche em decorrência da implementação de plano de assistência préescolar no órgão e entidade.'),
(222, '029.21', '', ' PUBLICAÇÃO E DIVULGAÇÃO', '', '', '', ' Incluem-se documentos referentes ao lançamento do programa de creche, à publicação de editais e à divulgação das vagas e dos critérios de seleção.'),
(223, '029.22', '', ' INSCRIÇÃO, SELEÇÃO, ADMISSÃO E RENOVAÇÃO', '', '', '', ' Incluem-se documentos referentes à inscrição, ao estabelecimento de níveis ou categorias de usuários do benefício, à avaliação socioeconômica do candidato e à comprovação de vínculo do beneficiário com o órgão e entidade, bem como termo de compromisso, listas de atualização de beneficiados e de renovação do benefício.'),
(224, '029.23', '', ' ACOMPANHAMENTO PEDAGÓGICO, MÉDICO E DO DESENVOLVIMENTO DA CRIANÇA', '', '', '', ' Incluem-se documentos referentes ao desenvolvimento de atividades recreativas e pedagógicas, ao fornecimento de alimentação e ao controle de saúde da criança.'),
(225, '029.24', '', ' AVALIAÇÃO DO PROGRAMA', '', '', '', ' Incluem-se documentos referentes às avaliações periódicas e permanentes do programa, tais como: laudos técnicos, estabelecimento de indicadores, estudos de ofertas e demandas e relatórios.'),
(226, '029.3', '', ' INCENTIVOS FUNCIONAIS', '', '', '', ' Incluem-se documentos referentes à concessão de prêmios, de medalhas, diplomas de honra ao mérito, condecorações e elogios.'),
(227, '029.4', '', ' DELEGAÇÃO DE COMPETÊNCIA E PROCURAÇÃO', '', '', '', ' Incluem-se documentos referentes à autorização ou à delegação que a autoridade administrativa responsável pelo órgão e entidade pode conferir a um servidor para tratar de matéria de competência exclusiva, tais como: ordenação de despesas, decisões de recursos e casos de edição de atos de caráter normativo.'),
(228, '029.5', '', ' CONTRATAÇÃO DE SERVIÇOS PROFISSIONAIS TRANSITÓRIOS', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa física para prestação de serviços devem ser classificados no código 018.2. Quanto aos documentos referentes às pessoas físicas contratadas (autônomos e colaboradores), que deverão integrar o assentamento funcional, classificar no código 020.12.'),
(229, '029.6', '', ' PETIÇÃO DE DIREITOS', '', '', '', ' Incluem-se requerimentos feitos pelo servidor em defesa de seus direitos ou interesses legítimos e pedidos de interposição de reconsideração ou de recurso.'),
(230, '030', '', ' GESTÃO DE MATERIAIS', '', '', '', ' Esta subclasse contempla documentos referentes à aquisição, movimentação, alienação, baixa e inventário de material permanente (mobiliário, equipamentos, utensílios, aparelhos, ferramentas, máquinas, instrumentos técnicos e obras de arte) e de consumo (material destinado às atividades de expediente, limpeza, manutenção, alimentação e abastecimento de veículos, medicamentos, uniformes, peças de reposição, matériasprimas e cobaias para uso científico), bem como aqueles referentes ao cadastramento e qualificação de fornecedores para a prestação produtos e serviços, e à execução de serviços de instalação e manutenção.'),
(231, '030.01', '', ' NORMATIZAÇÃO. REGULAMENTAÇÃO', '', '', '', ' Incluem-se documentos referentes às determinações legais, aos atos e instruções normativas, aos procedimentos operacionais e às decisões de caráter geral sobre a gestão de material permanente e de consumo.'),
(232, '030.02', '', ' CADASTRAMENTO DE FORNECEDORES E DE PRESTADORES DE SERVIÇOS', '', '', '', ' Incluem-se documentos referentes ao registro cadastral de fornecedores em sistema específico da administração pública, visando verificar a capacidade técnica para aquisições e contratações de serviços relacionadas à gestão de material.'),
(233, '030.03', '', ' ESPECIFICAÇÃO, PADRONIZAÇÃO, CODIFICAÇÃO, PREVISÃO, IDENTIFICAÇÃO E CLASSIFICAÇÃO', '', '', '', ' Incluem-se documentos referentes à especificação, padronização, codificação, previsão, identificação e classificação de material permanente, de consumo e de matériaprima e insumo, bem como catálogo e amostras de materiais.'),
(234, '030.04', '', ' QUALIFICAÇÃO DE FORNECEDORES', '', '', '', ' Incluem-se documentos referentes à seleção, préqualificação, qualificação, homologação, acompanhamento e auditoria de provedores externos, com base na capacidade de prover procedimentos, produtos e serviços, de acordo com requisitos técnicos e de qualidade.'),
(235, '031', '', ' AQUISIÇÃO E INCORPORAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição e incorporação de material permanente, de consumo e de matériaprima e insumo.'),
(236, '031.1', '', ' COMPRA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição definitiva de material permanente e de consumo. Nota: A compra poderá ocorrer nas modalidades de licitação, dispensa de licitação e inexigibilidade de licitação.'),
(237, '031.11', '', ' MATERIAL PERMANENTE', '', '', '', ' Incluem-se documentos referentes à compra e importação de material permanente. Quanto à aquisição e incorporação de acervos bibliográfico e museológico, classificar nas subdivisões do código 062.1.'),
(238, '031.12', '', ' MATERIAL DE CONSUMO', '', '', '', ' Incluem-se documentos referentes à compra e importação de material de consumo. Quanto à compra e importação de animais utilizados em pesquisas e estudos, mas que não sejam cobaias, classificar no código 041.13.'),
(239, '031.2', '', ' DOAÇÃO E PERMUTA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição definitiva de material permanente e de consumo.'),
(240, '031.21', '', ' MATERIAL PERMANENTE', '', '', '', ' Incluem-se documentos referentes à doação, permuta e transferência de material permanente.'),
(241, '031.22', '', ' MATERIAL DE CONSUMO', '', '', '', ' Incluem-se documentos referentes à doação, permuta e transferência de material de consumo.'),
(242, '031.3', '', ' DAÇÃO. ADJUDICAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição definitiva de material permanente e de consumo.'),
(243, '031.31', '', ' MATERIAL PERMANENTE', '', '', '', ' Incluem-se documentos referentes à dação e adjudicação de material permanente, para recebimento de parte ou totalidade de dívida.'),
(244, '031.32', '', ' MATERIAL DE CONSUMO', '', '', '', ' Incluem-se documentos referentes à dação e adjudicação de material de consumo, para recebimento de parte ou totalidade de dívida.'),
(245, '031.4', '', ' CESSÃO, COMODATO E EMPRÉSTIMO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição temporária de material permanente e de consumo.'),
(246, '031.41', '', ' MATERIAL PERMANENTE', '', '', '', ' Incluem-se documentos referentes à cessão, comodato e empréstimo de material permanente. Quanto ao empréstimo e devolução de material permanente disponibilizado aos servidores, classificar no código 039.2.'),
(247, '031.42', '', ' MATERIAL DE CONSUMO', '', '', '', ' Incluem-se documentos referentes à cessão, comodato e empréstimo de material de consumo.'),
(248, '031.5', '', ' LOCAÇÃO. ARRENDAMENTO MERCANTIL (LEASING)', '', '', '', ' Incluem-se documentos referentes à aquisição temporária de material permanente por locação e arrendamento mercantil (leasing).'),
(249, '032', '', ' MOVIMENTAÇÃO DE MATERIAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à movimentação de material permanente e de consumo, que envolve as atividades de controle, segurança e proteção do armazenamento, do deslocamento, da distribuição, da previsão de consumo e da reposição de estoque.'),
(250, '032.01', '', ' TERMO DE RESPONSABILIDADE. CAUTELA', '', '', '', ' Incluem-se documentos referentes à responsabilidade na movimentação de material e com o acautelamento no controle logístico, bem como as guias de transferência e os relatórios de movimentação de material.'),
(251, '032.1', '', ' CONTROLE DE ESTOQUE', '', '', '', ' Incluem-se documentos referentes à requisição e distribuição de material e à reposição e controle de estoque, bem como os relatórios de movimentação de almoxarifado. Quanto ao acompanhamento e controle de bens materiais, classificar nas subdivisões do código 036.'),
(252, '032.2', '', ' AUTORIZAÇÃO DE ENTRADA E SAÍDA DE MATERIAL', '', '', '', ' Incluem-se documentos referentes à autorização de entrada e saída de material, bem como aqueles referentes à armazenagem no decorrer do procedimento de desembaraço aduaneiro. Quanto ao empréstimo e devolução de material permanente disponibilizado aos servidores, classificar no código 039.2.'),
(253, '032.3', '', ' RECOLHIMENTO DE MATERIAL INSERVÍVEL AO DEPÓSITO', '', '', '', ' Incluem-se documentos referentes ao recolhimento de material inservível e de sucatas ao depósito do órgão e entidade. Quanto à coleta seletiva solidária e à reciclagem de resíduos descartáveis, classificar no código 017.1. Quanto à alienação definitiva, por desfazimento, de material permanente e de consumo em razão de serem considerados inservíveis e irrecuperáveis, classificar nas subdivisões do código 033.4.'),
(254, '032.4', '', ' TOMBAMENTO', '', '', '', ' Incluem-se documentos referentes ao tombamento de material permanente, pertencentes ao órgão e entidade, quando se tornam peças de exposição.'),
(255, '033', '', ' ALIENAÇÃO E BAIXA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à alienação e baixa de material permanente e de consumo.'),
(256, '033.1', '', ' VENDA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à alienação definitiva de material permanente e de consumo.'),
(257, '033.11', '', ' MATERIAL PERMANENTE', '', '', '', ' Incluem-se documentos referentes à venda e leilão de material permanente.'),
(258, '033.12', '', ' MATERIAL DE CONSUMO', '', '', '', ' Incluem-se documentos referentes à venda e leilão de material de consumo.'),
(259, '033.2', '', ' DOAÇÃO E PERMUTA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à alienação definitiva de material permanente e de consumo.'),
(260, '033.21', '', ' MATERIAL PERMANENTE', '', '', '', ' Incluem-se documentos referentes à doação, permuta e transferência de material permanente.'),
(261, '033.22', '', ' MATERIAL DE CONSUMO', '', '', '', ' Incluem-se documentos referentes à doação, permuta e transferência de material de consumo.'),
(262, '033.3', '', ' DAÇÃO. ADJUDICAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à alienação definitiva de material permanente e de consumo.'),
(263, '033.31', '', ' MATERIAL PERMANENTE', '', '', '', ' Incluem-se documentos referentes à dação e adjudicação de material permanente, para pagamento de parte ou totalidade de dívida.'),
(264, '033.32', '', ' MATERIAL DE CONSUMO', '', '', '', ' Incluem-se documentos referentes à dação e adjudicação de material de consumo, para pagamento de parte ou totalidade de dívida.'),
(265, '033.4', '', ' DESFAZIMENTO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à alienação definitiva de material permanente e de consumo em razão de serem considerados inservíveis e irrecuperáveis, ocorrendo por meio de inutilização, eliminação ou destruição. Quanto ao monitoramento e avaliação dos procedimentos de controle e preservação ambiental externa, classificar no código 017.2. Quanto ao recolhimento de material inservível e de sucatas ao depósito, classificar no código 032.3.'),
(266, '033.41', '', ' MATERIAL PERMANENTE', '', '', '', ' Incluem-se documentos referentes ao desfazimento de material permanente.'),
(267, '033.42', '', ' MATERIAL DE CONSUMO', '', '', '', ' Incluem-se documentos referentes ao desfazimento de material de consumo.'),
(268, '033.5', '', ' CESSÃO, COMODATO E EMPRÉSTIMO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à alienação temporária de material permanente e de consumo.'),
(269, '033.51', '', ' MATERIAL PERMANENTE', '', '', '', ' Incluem-se documentos referentes à cessão, comodato e empréstimo de material permanente. Quanto ao empréstimo e devolução de material permanente disponibilizado aos servidores, classificar no código 039.2.'),
(270, '033.52', '', ' MATERIAL DE CONSUMO', '', '', '', ' Incluem-se documentos referentes à cessão, comodato e empréstimo de material de consumo.'),
(271, '033.6', '', ' EXTRAVIO, ROUBO, DESAPARECIMENTO, FURTO E AVARIA', '', '', '', ' Incluem-se documentos referentes aos casos de extravio, roubo, desaparecimento, furto ou avaria de material. Quanto à apuração de responsabilidade disciplinar do servidor, classificar nas subdivisões do código 027. Quanto à ocorrência de sinistros em imóveis do órgão e entidade, classificar no código 046.3.'),
(272, '034', '', ' CONTRATAÇÃO DE PRESTAÇÃO DE SERVIÇOS', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1. Quanto à instalação e manutenção de equipamentos de combate a incêndio, classificar no código 046.13.'),
(273, '035', '', ' EXECUÇÃO DE SERVIÇOS DE INSTALAÇÃO E MANUTENÇÃO', '', '', '', ' Incluem-se documentos referentes à execução de serviços de instalação e manutenção de material, com meios próprios do órgão e entidade, não sendo necessária a contratação de empresas terceirizadas ou de mão de obra externa. Quanto à contratação de prestação de serviços para instalação, manutenção e reparo de material permanente, classificar nas subdivisões do código 018.'),
(274, '036', '', ' CONTROLE DE MATERIAIS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao acompanhamento e controle de bens materiais, que subsidiam a elaboração dos inventários. Quanto à requisição e distribuição de material e à reposição e controle de estoque, classificar no código 032.1. Quanto ao inventário de acervos bibliográfico e museológico, classificar no código 062.3.'),
(275, '036.01', '', ' COMISSÃO DE INVENTÁRIO', '', '', '', ' Incluem-se documentos referentes à constituição das comissões de inventário, bem como aqueles referentes à definição dos procedimentos para a realização dos mesmos.'),
(276, '036.1', '', ' INVENTÁRIO DE MATERIAL PERMANENTE', '', '', '', ' Incluem-se documentos referentes à elaboração do inventário do material permanente do órgão e entidade.'),
(277, '036.2', '', ' INVENTÁRIO DE MATERIAL DE CONSUMO', '', '', '', ' Incluem-se documentos referentes à elaboração do inventário do material de consumo do órgão e entidade.'),
(278, '039', '', ' OUTRAS AÇÕES REFERENTES À GESTÃO DE MATERIAIS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes a outras ações de gestão de bens materiais não contempladas nos descritores anteriores.'),
(279, '039.1', '', ' RACIONALIZAÇÃO DO USO DE MATERIAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à criação, designação, gestão, divulgação e resultados da atuação de grupos de trabalho e de comissões de racionalização do uso de material.'),
(280, '039.11', '', ' CRIAÇÃO E ATUAÇÃO DE GRUPOS DE TRABALHO', '', '', '', ' Incluem-se documentos referentes à criação de grupos de trabalho e comissões de racionalização do uso de material, aos registros das deliberações e às tomadas de decisão definidas nas reuniões, tais como: ato de instituição, regras para atuação, designação e substituição de membros, resoluções, atas e relatórios.'),
(281, '039.12', '', ' OPERACIONALIZAÇÃO DE REUNIÕES', '', '', '', ' Incluem-se documentos referentes à organização das reuniões dos grupos de trabalhos e das comissões de racionalização do uso de material, bem como aqueles referentes ao agendamento, convocação, pauta e lista de participantes.'),
(282, '039.2', '', ' EMPRÉSTIMO E DEVOLUÇÃO DE MATERIAL PERMANENTE', '', '', '', ' Incluem-se documentos referentes ao empréstimo e devolução de material permanente, normalmente, disponibilizados aos servidores, para uso em atividades internas e externas do órgão e entidade, como computadores portáteis, filmadoras, câmeras fotográficas e telefones celulares funcionais. Quanto à autorização de entrada e saída de material do órgão e entidade, classificar no código 032.2. Quanto ao empréstimo de material permanente para outro órgão e entidade, classificar no código 033.51.'),
(283, '040', '', ' GESTÃO DE BENS PATRIMONIAIS E DE SERVIÇOS', '', '', '', ' Esta subclasse contempla documentos referentes à aquisição, alienação e inventário de bens imóveis (terrenos, edifícios, residências e salas), de veículos motorizados (terrestres, fluviais, marítimos e aéreos) e não motorizados (propulsão humana e tração animal) e de bens semoventes (animais utilizados para patrulhamento, investigação e transporte), bem como aqueles referentes à contratação de prestação de serviços para o fornecimento de serviços públicos essenciais, para a execução de obras e ao controle, proteção, guarda e segurança patrimonial.'),
(284, '040.01', '', ' NORMATIZAÇÃO. REGULAMENTAÇÃO', '', '', '', ' Incluem-se documentos referentes às determinações legais, aos atos e instruções normativas, aos procedimentos operacionais e às decisões de caráter geral sobre a gestão de bens patrimoniais.'),
(285, '041', '', ' AQUISIÇÃO E INCORPORAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição e incorporação de bens imóveis, de veículos e de bens semoventes.'),
(286, '041.1', '', ' COMPRA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição definitiva de bens imóveis, de veículos e de bens semoventes. Nota: A compra poderá ocorrer nas modalidades de licitação, dispensa de licitação e inexigibilidade de licitação.'),
(287, '041.11', '', ' BENS IMÓVEIS', '', '', '', ' Incluem-se documentos referentes à compra e leilão de imóveis. Quanto à alienação definitiva e fiduciária de bens imóveis por venda, classificar no código 042.11.'),
(288, '041.12', '', ' VEÍCULOS', '', '', '', ' Incluem-se documentos referentes à compra e importação de veículos. Quanto à alienação definitiva e fiduciária de veículos por venda, classificar no código 042.12.'),
(289, '041.13', '', ' BENS SEMOVENTES', '', '', '', ' Incluem-se documentos referentes à compra e importação de animais. Quanto à compra e importação de animais utilizados como cobaias, classificar no código 031.12.'),
(290, '041.2', '', ' DOAÇÃO E PERMUTA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição definitiva de bens imóveis, de veículos e de bens semoventes.'),
(291, '041.21', '', ' BENS IMÓVEIS', '', '', '', ' Incluem-se documentos referentes a doação, permuta e transferência de imóveis.'),
(292, '041.22', '', ' VEÍCULOS', '', '', '', ' Incluem-se documentos referentes a doação, permuta e transferência de veículos.'),
(293, '041.23', '', ' BENS SEMOVENTES', '', '', '', ' Incluem-se documentos referentes a doação, permuta e transferência de animais.'),
(294, '041.3', '', ' DAÇÃO. ADJUDICAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição definitiva de bens imóveis e de veículos.'),
(295, '041.31', '', ' BENS IMÓVEIS', '', '', '', ' Incluem-se documentos referentes à dação e adjudicação de imóveis, para recebimento de parte ou totalidade de dívida.'),
(296, '041.32', '', ' VEÍCULOS', '', '', '', ' Incluem-se documentos referentes à dação e adjudicação de veículos, para recebimento de parte ou totalidade de dívida.'),
(297, '041.4', '', ' PROCRIAÇÃO', '', '', '', ' Incluem-se documentos referentes à aquisição definitiva de bens semoventes por procriação (animais nascidos no local de criação).'),
(298, '041.5', '', ' CESSÃO E COMODATO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição temporária de bens imóveis, de veículos e de bens semoventes.'),
(299, '041.51', '', ' BENS IMÓVEIS', '', '', '', ' Incluem-se documentos referentes à cessão e comodato de imóveis. Quanto ao uso de dependências do órgão e entidade, por servidores, classificar no código 043.7.'),
(300, '041.52', '', ' VEÍCULOS', '', '', '', ' Incluem-se documentos referentes à cessão e comodato de veículos.'),
(301, '041.53', '', ' BENS SEMOVENTES', '', '', '', ' Incluem-se documentos referentes à cessão e comodato de animais.'),
(302, '041.6', '', ' LOCAÇÃO. ARRENDAMENTO MERCANTIL (LEASING). SUBLOCAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição temporária de bens imóveis e de veículos.'),
(303, '041.61', '', ' BENS IMÓVEIS', '', '', '', ' Incluem-se documentos referentes à locação, arrendamento mercantil (leasing) e sublocação de imóveis. Quanto ao uso de dependências do órgão e entidade, por servidores, classificar no código 043.7. Quanto à locação temporária de salas e auditórios para realização de eventos, classificar no código 918.'),
(304, '041.62', '', ' VEÍCULOS', '', '', '', ' Incluem-se documentos referentes à locação, arrendamento mercantil (leasing) e sublocação de veículos.'),
(305, '041.7', '', ' DESMEMBRAMENTO', '', '', '', ' Incluem-se documentos referentes ao desmembramento de bem imóvel do órgão ou entidade, desvinculado da alienação. Quanto à alienação definitiva de bem imóvel por desmembramento, classificar no código 042.4.'),
(306, '042', '', ' ALIENAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à alienação de bens imóveis, de veículos e de bens semoventes.'),
(307, '042.1', '', ' VENDA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à alienação definitiva de bens imóveis, de veículos e de bens semoventes.'),
(308, '042.11', '', ' BENS IMÓVEIS', '', '', '', ' Incluem-se documentos referentes à venda e leilão de imóveis. Quanto à alienação fiduciária como garantia de propriedade de bens imóveis, classificar no código 041.11.'),
(309, '042.12', '', ' VEÍCULOS', '', '', '', ' Incluem-se documentos referentes à venda e leilão de veículos. Quanto à alienação fiduciária como garantia de propriedade de veículos, classificar no código 041.12.'),
(310, '042.13', '', ' BENS SEMOVENTES', '', '', '', ' Incluem-se documentos referentes à venda e leilão de animais.'),
(311, '042.2', '', ' DOAÇÃO E PERMUTA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à alienação definitiva de bens imóveis, de veículos e de bens semoventes.'),
(312, '042.21', '', ' BENS IMÓVEIS', '', '', '', ' Incluem-se documentos referentes à doação e permuta de imóveis.'),
(313, '042.22', '', ' VEÍCULOS', '', '', '', ' Incluem-se documentos referentes à doação e permuta de veículos.'),
(314, '042.23', '', ' BENS SEMOVENTES', '', '', '', ' Incluem-se documentos referentes à doação e permuta de animais.'),
(315, '042.3', '', ' DAÇÃO. ADJUDICAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à alienação definitiva de bens imóveis e de veículos.'),
(316, '042.31', '', ' BENS IMÓVEIS', '', '', '', ' Incluem-se documentos referentes à dação e adjudicação de bens imóveis, para pagamento de parte ou totalidade de dívida.'),
(317, '042.32', '', ' VEÍCULOS', '', '', '', ' Incluem-se documentos referentes à dação e adjudicação de veículos, para pagamento de parte ou totalidade de dívida.'),
(318, '042.4', '', ' DESAPROPRIAÇÃO E DESMEMBRAMENTO', '', '', '', ' Incluem-se documentos referentes à alienação definitiva de bens imóveis por desapropriação e por desmembramento. Quanto ao desmembramento de bem imóvel, desvinculado da alienação, classificar no código 041.7.'),
(319, '042.5', '', ' CESSÃO E COMODATO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à alienação temporária de bens imóveis, de veículos e de bens semoventes.'),
(320, '042.51', '', ' BENS IMÓVEIS', '', '', '', ' Incluem-se documentos referentes à cessão e comodato de imóveis.'),
(321, '042.52', '', ' VEÍCULOS', '', '', '', ' Incluem-se documentos referentes à cessão e comodato de veículos.'),
(322, '042.53', '', ' BENS SEMOVENTES', '', '', '', ' Incluem-se documentos referentes à cessão e comodato de animais.'),
(323, '042.6', '', ' LOCAÇÃO. ARRENDAMENTO. SUBLOCAÇÃO', '', '', '', ' Incluem-se documentos referentes à alienação temporária de bens imóveis por locação, por arrendamento e por sublocação, bem como os termos de permissão remunerada de uso.'),
(324, '042.7', '', ' BAIXA. DESFAZIMENTO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à baixa e desfazimento de veículos e de bens semoventes.'),
(325, '042.71', '', ' VEÍCULOS', '', '', '', ' Incluem-se documentos referentes à baixa e desfazimento de veículos, quando o mesmo é retirado de circulação, por ser irrecuperável e inservível, definitivamente desmontado, sinistrado com laudo de perda total, vendido ou leiloado como sucata.'),
(326, '042.72', '', ' BENS SEMOVENTES', '', '', '', ' Incluem-se documentos referentes à baixa de animais por incapacidade, inaptidão, invalidez, morte, sacrifício e aposentadoria.'),
(327, '043', '', ' ADMINISTRAÇÃO CONDOMINIAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos procedimentos necessários para a administração, o registro e o controle dos bens patrimoniais do órgão e entidade.');
INSERT INTO `ttddnew1` (`id`, `symbol`, `kind`, `assunto`, `fase_corrente`, `fase_intermediaria`, `destinacao`, `observacoes`) VALUES
(328, '043.1', '', ' REGISTRO DOS IMÓVEIS', '', '', '', ' Incluem-se os documentos referentes à identificação e ao registro dos imóveis, tais como: título de propriedade, escrituras, certidões, plantas e projetos arquitetônicos.'),
(329, '043.2', '', ' SERVIÇOS DE CONDOMÍNIO', '', '', '', ' Incluem-se documentos referentes ao pagamento de despesas condominiais dos imóveis. Quanto à documentação normativa, como convenção de condomínio, regulamento interno e atas de assembleias, classificar no código 040.01. Quanto ao pagamento de taxas e tributos, como o imposto predial e territorial urbano e a taxa de incêndio, classificar no código 059.2.'),
(330, '043.3', '', ' REINTEGRAÇÃO DE POSSE. REIVINDICAÇÃO DE DOMÍNIO. DESPEJO DE PERMISSIONÁRIO', '', '', '', ' Incluem-se documentos referentes à reintegração de posse, à reivindicação de domínio e ao despejo de permissionário dos imóveis do órgão e entidade.'),
(331, '043.4', '', ' TOMBAMENTO', '', '', '', ' Incluem-se documentos referentes ao tombamento de imóveis pertencentes ao órgão e entidade, quando estes forem declarados como patrimônio históricocultural por instituição competente e responsável por esta atribuição.'),
(332, '043.5', '', ' INSPEÇÃO PATRIMONIAL', '', '', '', ' Incluem-se documentos referentes às inspeções patrimoniais (preventivas e periódicas) realizadas nas propriedades do órgão e entidade, bem como a entrega de notificações sobre as infrações verificadas, como registro de invasões, grilagem, ações de vandalismo, obstrução de passagem, ocupação ilegal, utilização inadequada de imóvel, construção irregular e construção não autorizada.'),
(333, '043.6', '', ' MUDANÇA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à transferência de bens materiais do órgão e entidade. Quanto à contratação de serviços de mudanças, classificar nas subdivisões do código 018.'),
(334, '043.61', '', ' PARA OUTROS IMÓVEIS', '', '', '', ' Incluem-se documentos referentes à mudança de bens materiais realizada de um imóvel para outro imóvel do órgão e entidade.'),
(335, '043.62', '', ' DENTRO DO MESMO IMÓVEL', '', '', '', ' Incluem-se documentos referentes à mudança de bens materiais realizada dentro do mesmo imóvel do órgão e entidade.'),
(336, '043.7', '', ' USO DE DEPENDÊNCIAS', '', '', '', ' Incluem-se documentos referentes à requisição e utilização, por servidores, de dependências (salas e auditórios) de imóvel do órgão e entidade. Quanto ao uso de dependências do órgão e entidade, por terceiros, mediante cessão e comodato, classificar no código 041.51. Quanto ao uso de dependências do órgão e entidade, por terceiros, mediante locação, arrendamento mercantil (leasing) e sublocação, classificar no código 041.61.'),
(337, '044', '', ' ADMINISTRAÇÃO DA FROTA DE VEÍCULOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos procedimentos necessários para a administração e o controle de uso dos veículos do órgão e entidade.'),
(338, '044.1', '', ' CADASTRAMENTO, LICENCIAMENTO E EMPLACAMENTO', '', '', '', ' Incluem-se documentos referentes ao cadastramento, licenciamento e emplacamento de veículos. Quanto ao pagamento de taxas e tributos dos veículos, como o imposto sobre a propriedade de veículos automotores, classificar no código 059.2.'),
(339, '044.2', '', ' TOMBAMENTO', '', '', '', ' Incluem-se documentos referentes ao tombamento de veículos, pertencentes ao órgão e entidade, quando se tornam peças de exposição.'),
(340, '044.3', '', ' OCORRÊNCIA DE SINISTROS', '', '', '', ' Incluem-se documentos referentes a acidentes, eventos inesperados e não premeditados ocorridos com veículos pertencentes ao órgão e entidade e com veículos locados, tais como: colisão, incêndio, roubo, furto, enchente e alagamento. Quanto à ocorrência de sinistros em imóveis do órgão e entidade, classificar no código 046.3.'),
(341, '044.4', '', ' CONTROLE DE USO', '', '', '', ' Incluem-se documentos referentes ao controle e a utilização de veículos, bem como aqueles referentes à requisição e autorização para uso, dentro e fora do horário de expediente. Quanto ao controle de entrada e saída de veículos de visitantes e prestadores de serviço, classificar no código 046.4.'),
(342, '044.5', '', ' ESTACIONAMENTO. GARAGEM', '', '', '', ' Incluem-se documentos referentes à utilização de estacionamento e garagem, do órgão e entidade, por veículos oficiais e por veículos dos servidores. Quanto ao controle de entrada e saída de veículos de visitantes e de prestadores de serviço, classificar no código 046.4.'),
(343, '044.6', '', ' NOTIFICAÇÕES DE INFRAÇÕES E MULTAS', '', '', '', ' Incluem-se documentos referentes às infrações de trânsito e multas relativas aos veículos pertencentes ao órgão e entidade e aos veículos locados, bem como aqueles referentes às sindicâncias instauradas.'),
(344, '045', '', ' CONTRATAÇÃO DE PRESTAÇÃO DE SERVIÇOS', '', '', '', ' Os descritores 045.01, 045.2, 045.21, 045.22, 045.23, 045.24, 045.4, 045.5, 045.6 e 045.7 foram suprimidos em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica ou física para prestação de serviços devem ser classificados nas subdivisões do código 018. Só foram mantidos os descritores relativos à contratação para fornecimento de serviços públicos essenciais e para a execução de obras em bens imóveis do órgão e entidade.'),
(345, '045.01', '', ' SEGURO PATRIMONIAL', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1. Quanto aos seguros destinados aos servidores, classificar no código 023.91.'),
(346, '045.1', '', ' FORNECIMENTO DE SERVIÇOS PÚBLICOS ESSENCIAIS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à contratação de empresas concessionárias de serviços públicos essenciais.'),
(347, '045.11', '', ' ÁGUA E ESGOTAMENTO SANITÁRIO', '', '', '', ' Incluem-se documentos referentes ao serviço contratado para fornecimento de água e esgotamento sanitário.'),
(348, '045.12', '', ' GÁS', '', '', '', ' Incluem-se documentos referentes ao serviço contratado para fornecimento de gás. Quanto à aquisição de gás e outros materiais engarrafados de uso industrial, para tratamento de água, iluminação e uso médico, bem como gases nobres para uso em laboratório científico, classificar no código 031.12.'),
(349, '045.13', '', ' ENERGIA ELÉTRICA', '', '', '', ' Incluem-se documentos referentes ao serviço contratado para fornecimento de energia elétrica.'),
(350, '045.2', '', ' MANUTENÇÃO E REPARO DAS INSTALAÇÕES', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(351, '045.21', '', ' ELEVADORES', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(352, '045.22', '', ' SISTEMAS CENTRAIS DE ARCONDICIONADO', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(353, '045.23', '', ' SUBESTAÇÕES ELÉTRICAS E GERADORES', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(354, '045.24', '', ' CONSERVAÇÃO PREDIAL', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(355, '045.3', '', ' EXECUÇÃO DE OBRAS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à contratação de pessoa jurídica ou física para a execução de obras em bens imóveis do órgão e entidade, bem como aqueles referentes à prestação de consultoria para a realização de obras. Quanto à contratação de pessoa jurídica ou física para prestação de demais serviços, distintos da execução de obras, classificar nas subdivisões do código 018.'),
(356, '045.31', '', ' CONSTRUÇÃO', '', '', '', ' Incluem-se documentos referentes às obras para a construção de bem imóvel.'),
(357, '045.32', '', ' REFORMA. RECUPERAÇÃO. RESTAURAÇÃO', '', '', '', ' Incluem-se documentos referentes à reforma, recuperação e/ou restauração de bem imóvel do órgão e entidade, como troca de piso, reforma de sala, recuperação de rampa de acesso, restauração da fachada, troca de encanamento, troca de fiação elétrica, pintura interna e externa e impermeabilização de telhado.'),
(358, '045.33', '', ' ADAPTAÇÃO DE USO', '', '', '', ' Incluem-se documentos referentes às obras de adaptação de uso de bem imóvel do órgão e entidade, tombado ou não, inclusive para acessibilidade.'),
(359, '045.4', '', ' VIGILÂNCIA PATRIMONIAL', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1. Quanto à contratação de bombeiro profissional civil como autônomo (pessoa física), classificar no código 018.2. Quanto à constituição de brigada voluntária de incêndio, classificar no código 046.12. Quanto ao funcionamento de sistemas eletrônicos de monitoramento e vigilância, classificar no código 046.2.'),
(360, '045.5', '', ' ABASTECIMENTO E MANUTENÇÃO DE VEÍCULOS', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1. Quanto à aquisição de combustíveis, lubrificantes e peças de reposição de forma isolada, classificar no código 031.12.'),
(361, '045.6', '', ' ASSISTÊNCIA VETERINÁRIA', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(362, '045.7', '', ' ADESTRAMENTO', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(363, '046', '', ' PROTEÇÃO, GUARDA E SEGURANÇA PATRIMONIAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às atividades para proteção, guarda e segurança das dependências do órgão e entidade.'),
(364, '046.1', '', ' PREVENÇÃO DE INCÊNDIO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao treinamento de pessoal, instalação e manutenção de extintores, inspeções periódicas dos equipamentos de combate a incêndio e constituição de brigadas voluntárias de incêndio.'),
(365, '046.11', '', ' PLANEJAMENTO, ELABORAÇÃO E ACOMPANHAMENTO DE PROJETOS', '', '', '', ' Incluem-se documentos referentes ao planejamento, à elaboração e à execução de projetos para a prevenção de incêndios, bem como os relatórios de acompanhamento.'),
(366, '046.12', '', ' CONSTITUIÇÃO DE BRIGADA VOLUNTÁRIA', '', '', '', ' Incluem-se documentos referentes à constituição de brigada de incêndio, composta por servidores que se apresentam como voluntários. Quanto à contratação de bombeiro profissional civil como autônomo (pessoa física), classificar no código 018.2. Quanto à contratação de empresa terceirizada para execução de serviços de brigada de incêndio (bombeiro profissional civil), classificar no código 018.1.'),
(367, '046.13', '', ' INSTALAÇÃO E MANUTENÇÃO DE EQUIPAMENTOS DE COMBATE A INCÊNDIO', '', '', '', ' Incluem-se documentos referentes à instalação e à manutenção de equipamentos e sistemas de combate a incêndio, como extintores, mangueiras, machados, luvas e capacetes.'),
(368, '046.2', '', ' MONITORAMENTO. VIGILÂNCIA', '', '', '', ' Incluem-se documentos referentes ao funcionamento de sistemas eletrônicos de monitoramento e vigilância que utilizam câmeras de circuitos fechados de televisão, instalados nas dependências do órgão e entidade, bem como as gravações resultantes do mesmo. Quanto à instalação, conservação e reparo dos equipamentos, classificar no código 034. Quanto à contratação de empresa terceirizada para execução de serviços de guarda e segurança patrimonial, classificar no código 018.1.'),
(369, '046.3', '', ' OCORRÊNCIA DE SINISTROS', '', '', '', ' Incluem-se documentos referentes às vistorias, sindicâncias e perícias técnicas relativas ao arrombamento, incêndio, explosão, vandalismo e danos causados por intempéries (raio, vendaval e granizo) em imóveis do órgão e entidade. Quanto ao extravio, roubo, desaparecimento, furto e avaria de material permanente e de consumo, classificar no código 033.6. Quanto à ocorrência de sinistros em veículos do órgão e entidade, classificar no código 044.3. Quanto ao controle das áreas de armazenamento, classificar no código 064.2.'),
(370, '046.4', '', ' CONTROLE DE PORTARIA', '', '', '', ' Incluem-se documentos referentes ao controle de entrada e saída de pessoas, materiais, veículos, visitantes e prestadores de serviço, à permissão para entrada e permanência fora do horário de expediente, ao controle de entrega e devolução de chaves e ao registro de ocorrências. Quanto ao controle de uso de veículos, classificar no código 044.4. Quanto à utilização de estacionamento e garagem, do órgão e entidade, por veículos oficiais e por veículos dos servidores, classificar no código 044.5.'),
(371, '047', '', ' CONTROLE DE BENS PATRIMONIAIS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao acompanhamento e ao controle de bens patrimoniais, que subsidiam a elaboração dos inventários.'),
(372, '047.01', '', ' COMISSÃO DE INVENTÁRIO', '', '', '', ' Incluem-se os documentos referentes à constituição das comissões de inventário, bem como aqueles referentes à definição dos procedimentos para a realização dos mesmos.'),
(373, '047.1', '', ' INVENTÁRIO DE BENS IMÓVEIS', '', '', '', ' Incluem-se documentos referentes ao levantamento da situação dos imóveis que se encontram em uso e à verificação da disponibilidade dos imóveis do órgão e entidade, bem como aqueles referentes à emissão de relatórios.'),
(374, '047.2', '', ' INVENTÁRIO DE VEÍCULOS', '', '', '', ' Incluem-se documentos referentes ao levantamento da situação dos veículos que se encontram em uso e à verificação da disponibilidade dos veículos do órgão e entidade, bem como aqueles referentes à emissão de relatórios.'),
(375, '047.3', '', ' INVENTÁRIO DE BENS SEMOVENTES', '', '', '', ' Incluem-se documentos referentes ao levantamento dos animais que se encontram em uso e à verificação da disponibilidade dos animais do órgão e entidade, bem como aqueles referentes à emissão de relatórios.'),
(376, '049', '', ' OUTRAS AÇÕES REFERENTES À GESTÃO DE BENS PATRIMONIAIS E DE SERVIÇOS', '', '', '', ' Nas subdivisões deste grupo classificam-se documentos referentes a outras ações de gestão de bens patrimoniais não contempladas nos descritores anteriores.'),
(377, '049.1', '', ' RACIONALIZAÇÃO DO USO DE BENS E SERVIÇOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à criação, designação, gestão, divulgação e resultados da atuação de grupos de estudo e de comissões de racionalização do uso dos bens patrimoniais e dos serviços públicos essenciais, como redução de uso de veículo oficial e a economia de água e de energia elétrica.'),
(378, '049.11', '', ' CRIAÇÃO E ATUAÇÃO DE GRUPOS DE TRABALHO', '', '', '', ' Incluem-se documentos referentes à criação de grupos de trabalho e comissões de racionalização do uso de bens e serviços, aos registros das deliberações e às tomadas de decisão definidas nas reuniões, tais como: ato de instituição, regras para atuação, designação e substituição de membros, resoluções, atas e relatórios.'),
(379, '049.12', '', ' OPERACIONALIZAÇÃO DE REUNIÕES', '', '', '', ' Incluem-se documentos referentes à organização das reuniões dos grupos de trabalhos e das comissões de racionalização do uso de bens e serviços, bem como os referentes ao agendamento, convocação, pauta e lista de participantes.'),
(380, '050', '', ' GESTÃO ORÇAMENTÁRIA E FINANCEIRA', '', '', '', ' Esta subclasse contempla documentos referentes às atividades de programação e elaboração do orçamento, à gestão e execução orçamentária e financeira, às operações bancárias e ao controle externo das atividades financeiras do órgão e entidade.'),
(381, '050.01', '', ' NORMATIZAÇÃO. REGULAMENTAÇÃO', '', '', '', ' Incluem-se documentos referentes às determinações legais, aos atos e instruções normativas, aos procedimentos operacionais e às decisões de caráter geral sobre a gestão do orçamento e das finanças do órgão e entidade.'),
(382, '050.02', '', ' CONFORMIDADE DE REGISTRO DE GESTÃO', '', '', '', ' Incluem-se documentos referentes à conformidade diária e à conformidade de suporte documental, bem como aqueles que comprovam e dão suporte às operações registradas. Quanto à consolidação dos registros contábeis, classificar em 052.23.'),
(383, '050.03', '', ' CONFORMIDADE CONTÁBIL', '', '', '', ' Incluem-se documentos referentes aos demonstrativos contábeis decorrentes dos registros da execução orçamentária, financeira e patrimonial, gerados por meio dos sistemas de administração financeira da administração pública. Quanto à consolidação dos registros contábeis, classificar em 052.23.'),
(384, '051', '', ' GESTÃO ORÇAMENTÁRIA', '', '', '', ' Nas subdivisões deste descritor classificam-se os documentos referentes às atividades de programação, gestão e execução do orçamento do órgão e entidade.'),
(385, '051.1', '', ' PROGRAMAÇÃO ORÇAMENTÁRIA', '', '', '', ' Incluem-se documentos referentes à elaboração do orçamento, como a programação, a previsão e a proposta orçamentária, os estudos de adequação da estrutura programática, a atualização e o aperfeiçoamento das informações constantes do cadastro de ações orçamentárias, a fixação dos referenciais monetários para apresentação das propostas orçamentárias e dos limites de movimentação, de empenho e de pagamento das unidades administrativas do órgão e entidade.'),
(386, '051.2', '', ' DETALHAMENTO DE DESPESA', '', '', '', ' Incluem-se documentos referentes ao estabelecimento das metas e à destinação de recursos e receitas por fontes, bem como o detalhamento, a nível operacional, dos projetos e atividades a serem desenvolvidas no exercício, especificando os elementos de despesa e respectivos desdobramentos.'),
(387, '051.3', '', ' EXECUÇÃO ORÇAMENTÁRIA', '', '', '', ' Incluem-se documentos referentes à movimentação, interna e externa, e à descentralização de créditos orçamentários pelo órgão e entidade, bem como as transferências, provisões, destaques, estornos e subvenções'),
(388, '051.4', '', ' RETIFICAÇÃO ORÇAMENTÁRIA. CRÉDITOS ADICIONAIS', '', '', '', ' Incluem-se documentos referentes às autorizações de despesas não computadas, ou insuficientemente dotadas, na lei orçamentária, por meio da liberação de créditos suplementares, especiais e extraordinários.'),
(389, '052', '', ' GESTÃO FINANCEIRA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às atividades de programação, gestão e execução financeira do órgão e entidade, bem como aqueles referentes ao controle da efetiva entrada e saída de recursos do órgão e entidade.'),
(390, '052.1', '', ' PROGRAMAÇÃO FINANCEIRA', '', '', '', ' Incluem-se documentos referentes à programação da entrada e da saída de recursos financeiros, à previsão da utilização dos recursos destinados a determinados fins e à operacionalização de gastos específicos visando a realização das atividades e projetos atribuídos às unidades orçamentárias, bem como aqueles referentes ao contingenciamento do orçamento do órgão e entidade.'),
(391, '052.2', '', ' EXECUÇÃO FINANCEIRA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao controle da entrada e saída de recursos financeiros do órgão e entidade.'),
(392, '052.21', '', ' RECEITA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos ingressos de recursos nos cofres da administração pública, que se desdobram em receitas orçamentárias, quando representam disponibilidades de recursos financeiros para o erário, e em ingressos extraorçamentários, quando representam entradas compensatórias.'),
(393, '052.211', '', ' RECEITA CORRENTE', '', '', '', ' Incluem-se documentos referentes às receitas provenientes de tributos, de contribuições, da exploração do patrimônio estatal (patrimonial), da exploração de atividades econômicas (agropecuária, industrial e de serviços) e de recursos financeiros recebidos de outras pessoas de direito público ou privado destinados a atender as despesas correntes do órgão e entidade. Quanto ao estabelecimento de preços e tarifas pela prestação de serviços por parte do órgão e entidade, classificar no código 059.3. Quanto à devolução ao erário, classificar no código 059.4. Quanto à restituição de rendas arrecadadas, classificar no código 059.5.'),
(394, '052.212', '', ' RECEITA DE CAPITAL', '', '', '', ' Incluem-se documentos referentes às receitas provenientes de recursos financeiros oriundos da constituição de dívidas, da conversão, em espécie, de bens e direitos, da venda de ações e do recebimento de recursos de outras pessoas de direito público ou privado destinadas a atender despesas de capital e ao superávit do orçamento corrente. Quanto à compra e subscrição de ações, classificar no código 059.1.'),
(395, '052.213', '', ' INGRESSO EXTRAORÇAMENTÁRIO', '', '', '', ' Incluem-se documentos referentes aos recursos financeiros de caráter temporário, que não estão previstos na lei orçamentária anual, como os depósitos em caução, as fianças, a antecipação de receita orçamentária, a emissão de moeda e as entradas compensatórias no ativo e passivo financeiro.'),
(396, '052.22', '', ' DESPESA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à utilização de recursos, fixados e especificados na Lei Orçamentária Anual (LOA), para pagamento das despesas efetuadas pelo órgão e entidade.'),
(397, '052.221', '', ' DESPESA CORRENTE', '', '', '', ' Incluem-se documentos referentes à utilização dos recursos provenientes das dotações orçamentárias ordinárias destinadas à manutenção contínua dos serviços públicos, como as despesas de custeio reservadas ao pagamento de pessoal (ativos, inativos, pensionistas, civis e militares) e dos encargos sociais, bem como aqueles referentes às transferências correntes relacionadas às contribuições e subvenções que atendem à manifestação de outras entidades de direito público ou privado, entre elas as transferências de assistência e previdência social e os pagamentos dos juros e encargos da dívida pública. Quanto às despesas decorrentes da compra de material de consumo, classificar no código 031.12. Quanto às despesas decorrentes da contratação de empresas terceirizadas para a prestação de serviços, classificar no código 018.1.'),
(398, '052.222', '', ' DESPESA DE CAPITAL', '', '', '', ' Incluem-se documentos referentes à utilização dos recursos provenientes das dotações orçamentárias ordinárias destinadas aos investimentos, às inversões financeiras e à amortização da dívida interna e externa. Quanto às despesas decorrentes da compra de material permanente, classificar no código 031.11. Quanto às despesas decorrentes da compra de bens imóveis, classificar no código 041.11. Quanto às despesas decorrentes da contratação de empresas terceirizadas para a execução de obras em bens imóveis do órgão e entidade, classificar nas subdivisões do código 045.3.'),
(399, '052.23', '', ' DEMONSTRAÇÃO CONTÁBIL', '', '', '', ' Incluem-se os documentos referentes à consolidação dos registros contábeis, tais como: livrorazão, balanço patrimonial, balanço orçamentário, balanço financeiro, demonstração das variações patrimoniais, demonstrações dos fluxos de caixa, demonstração das mutações do patrimônio líquido, demonstração do resultado econômico e balancetes. Quanto à conformidade de registro de gestão, classificar no código 050.02. Quanto à conformidade contábil, classificar no código 050.03.'),
(400, '052.24', '', ' GESTÃO DE FUNDOS ESPECIAIS', '', '', '', ' Incluem-se documentos referentes à administração de receitas específicas vinculadas por lei à realização de determinados objetivos ou serviços.'),
(401, '052.25', '', ' GERENCIAMENTO DE BENEFÍCIOS, ESTÍMULOS E INCENTIVOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos benefícios, estímulos e incentivos concedidos ao órgão e entidade.'),
(402, '052.251', '', ' FINANCEIROS', '', '', '', ' Incluem-se documentos referentes aos desembolsos efetivos realizados por meio das equalizações de juros e preços, bem como aqueles referentes à assunção das dívidas decorrentes de saldos de obrigações de responsabilidade do Tesouro Nacional, cujos valores constam da lei orçamentária da União.'),
(403, '052.252', '', ' CREDITÍCIOS', '', '', '', ' Incluem-se documentos referentes aos subsídios, incentivos fiscais e investimentos destinados ao financiamento de atividades produtivas e voltados ao desenvolvimento econômico regional e social, bem como aqueles referentes aos gastos decorrentes de programas oficiais de crédito, operacionalizados por meio de fundos ou programas com taxa de juros inferior ao custo de captação do governo federal.'),
(404, '053', '', ' OPERAÇÃO BANCÁRIA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às operações de conciliação bancária, aos pagamentos em moeda estrangeira e à movimentação de conta única do Tesouro Nacional e de outras contas correntes. Quanto à ContaDepósito Vinculada  bloqueada para movimentação (instrumento de gestão de risco para as contratações de prestação de serviços continuados), classificar no código 018.1.'),
(405, '053.01', '', ' CONCILIAÇÃO BANCÁRIA', '', '', '', ' Incluem-se documentos referentes às conciliações das contas correntes e contábeis, com o controle financeiro interno do órgão e entidade.'),
(406, '053.1', '', ' PAGAMENTO EM MOEDA ESTRANGEIRA', '', '', '', ' Incluem-se documentos referentes aos pagamentos em moedas diferentes da moeda nacional.'),
(407, '053.2', '', ' GESTÃO DA CONTA ÚNICA', '', '', '', ' Incluem-se documentos referentes à transferência de recursos recebidos na rede bancária para a conta única, com os créditos das respectivas unidades gestoras, bem como aqueles referentes às assinaturas autorizadas, às ordens bancárias e aos extratos de contas.'),
(408, '053.3', '', ' GESTÃO DE CONTAS CORRENTES BANCÁRIAS: TIPO A, B, C, D e E', '', '', '', ' Incluem-se documentos referentes à abertura, movimentação e encerramento das contas correntes, bem como aqueles referentes às assinaturas autorizadas, às ordens bancárias e aos extratos de contas.'),
(409, '053.4', '', ' GESTÃO DE CONTAS ESPECIAIS', '', '', '', ' Incluem-se documentos referentes à movimentação dos recursos advindos de empréstimos e créditos externos.'),
(410, '054', '', ' CONTROLE EXTERNO. AUDITORIA EXTERNA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às auditorias de contas do órgão e entidade, realizadas pelos órgãos de controle externo. Quanto ao controle interno e à auditoria interna, classificar no código 003.1.'),
(411, '054.1', '', ' PRESTAÇÃO DE CONTAS. TOMADA DE CONTAS', '', '', '', ' Incluem-se documentos referentes à prestação e à tomada de contas realizadas em razão da solicitação do órgão responsável pela auditoria das contas da administração pública, bem como os relatórios, pareceres e certificados de julgamento das contas.'),
(412, '054.2', '', ' TOMADA DE CONTAS ESPECIAL', '', '', '', ' Incluem-se documentos referentes à instauração de tomada de contas especial pelo órgão responsável pela auditoria das contas da administração pública, bem como os relatórios, pareceres e certificados de julgamento das contas.'),
(413, '059', '', ' OUTRAS AÇÕES REFERENTES À GESTÃO ORÇAMENTÁRIA E FINANCEIRA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes a outras ações de gestão orçamentária e financeira não contempladas nos descritores anteriores.'),
(414, '059.1', '', ' COMPRA E SUBSCRIÇÃO DE AÇÕES', '', '', '', ' Incluem-se documentos referentes à compra e ao direito de subscrever ações das sociedades de economia mista. Quanto à venda de ações, classificar no código 052.212.'),
(415, '059.2', '', ' RECOLHIMENTO DE IMPOSTOS, TAXAS E DEMAIS TRIBUTOS', '', '', '', ' Incluem-se documentos referentes ao pagamento de impostos, taxas e demais tributos pelo órgão e entidade, tais como: taxa de incêndio, contribuição de melhoria, imposto predial e territorial urbano. Quanto ao pagamento dos encargos patronais e recolhimentos efetuados pelo empregador, classificar nas subdivisões do código 023.18. Quanto ao pagamento das despesas condominiais dos bens imóveis, classificar no código 043.2.'),
(416, '059.3', '', ' FIXAÇÃO DE CUSTOS DE SERVIÇOS', '', '', '', ' Incluem-se documentos referentes ao estabelecimento de preços e tarifas pela prestação de serviços por parte do órgão e entidade. Quanto ao recebimento de recursos financeiros recebidos da prestação de serviços por parte do órgão e entidade, classificar no código 052.211.'),
(417, '059.4', '', ' DEVOLUÇÃO AO ERÁRIO', '', '', '', ' Incluem-se documentos referentes à restituição ao Erário de valores percebidos indevidamente pelo servidor. Quanto aos pedidos de retificação de pagamento feitos pelo servidor, classificar no código 023.191. Quanto à receita corrente, classificar no código 052.211.'),
(418, '059.5', '', ' RESTITUIÇÃO DE RENDAS ARRECADADAS', '', '', '', ' Incluem-se documentos referentes à solicitação de restituição, total ou parcial, ao cidadão, de receitas recolhidas a maior ou indevidamente, por meio de Guia de Recolhimento da União (GRU). Quanto à receita corrente, classificar no código 052.211.'),
(419, '060', '', ' GESTÃO DA DOCUMENTAÇÃO E DA INFORMAÇÃO', '', '', '', ' Esta subclasse contempla documentos referentes à produção, controle, classificação, avaliação, destinação e tratamento técnico da documentação arquivística, à aquisição, processamento técnico, controle, distribuição e acesso aos acervos bibliográfico e museológico, bem como aqueles referentes à conservação e preservação de acervos, à produção editorial e à gestão de sistemas e de infraestrutura tecnológica do órgão e entidade.'),
(420, '060.01', '', ' NORMATIZAÇÃO. REGULAMENTAÇÃO', '', '', '', ' Incluem-se documentos referentes às determinações legais, aos atos e instruções normativas, aos procedimentos operacionais e às decisões de caráter geral sobre a gestão da documentação e da informação do órgão e entidade.'),
(421, '061', '', ' GESTÃO DE DOCUMENTOS DE ARQUIVO', '', '', '', ' Nas subdivisões desse descritor classificam-se documentos referentes à elaboração e implantação de programas de gestão da documentação arquivística.'),
(422, '061.01', '', ' CONSTITUIÇÃO DA COMISSÃO PERMANENTE DE AVALIAÇÃO DE DOCUMENTOS (CPAD)', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos procedimentos para a constituição e atuação da comissão.'),
(423, '061.011', '', ' COMPOSIÇÃO E ATUAÇÃO', '', '', '', ' Incluem-se documentos referentes à constituição da CPAD, à indicação, designação e substituição de membros e à instalação e posse da comissão, bem como aqueles referentes ao registro das deliberações e às tomadas de decisões, resoluções, atas e relatórios.'),
(424, '061.012', '', ' OPERACIONALIZAÇÃO DE REUNIÕES', '', '', '', ' Incluem-se documentos referentes à organização das reuniões da CPAD, bem como aqueles referentes ao agendamento, convocação, pauta e lista de participantes.'),
(425, '061.1', '', ' ADOÇÃO E CONTROLE DOS PROCEDIMENTOS DE PROTOCOLO', '', '', '', ' Incluem-se documentos referentes ao controle da entrada, da distribuição, da movimentação e da expedição de documentos, bem como aqueles referentes aos procedimentos para autuação dos documentos avulsos. Quanto aos documentos referentes ao controle da mudança de acervos, classificar no código 063.4.'),
(426, '061.2', '', ' ANÁLISE DA SITUAÇÃO ARQUIVÍSTICA', '', '', '', ' Incluem-se documentos referentes à realização de diagnóstico da situação dos serviços de gestão de documentos de arquivo do órgão e entidade.'),
(427, '061.3', '', ' LEVANTAMENTO DA PRODUÇÃO E DO FLUXO DOCUMENTAL', '', '', '', ' Incluem-se documentos referentes ao estudo e ao levantamento da produção e do fluxo documental para subsidiar a elaboração dos instrumentos técnicos de gestão de documentos.'),
(428, '061.4', '', ' ELABORAÇÃO DOS INSTRUMENTOS TÉCNICOS DE GESTÃO DE DOCUMENTOS', '', '', '', ' Incluem-se documentos referentes à elaboração do código de classificação e da tabela de temporalidade e destinação de documentos de arquivo relativos às atividadesfim do órgão e entidade.'),
(429, '061.5', '', ' APLICAÇÃO DOS INSTRUMENTOS TÉCNICOS DE GESTÃO DE DOCUMENTOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aplicação do código de classificação e da tabela de temporalidade e destinação de documentos de arquivo relativos às atividadesmeio e atividadesfim do órgão e entidade.'),
(430, '061.51', '', ' CLASSIFICAÇÃO', '', '', '', ' Incluem-se documentos referentes aos procedimentos para aplicação dos códigos de classificação de documentos de arquivo relativos às atividadesmeio e atividadesfim do órgão e entidade, bem como aqueles referentes à metodologia utilizada para a ordenação dos documentos.'),
(431, '061.52', '', ' AVALIAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos procedimentos para aplicação dos prazos de guarda e da destinação final definidos nas tabelas de temporalidade e destinação de documentos de arquivo relativas às atividadesmeio e atividadesfim do órgão e entidade, bem como aqueles referentes à metodologia utilizada para a seleção dos documentos.'),
(432, '061.521', '', ' ELIMINAÇÃO', '', '', '', ' Incluem-se documentos referentes aos procedimentos para análise e seleção de documentos de arquivo, bem como aqueles referentes à elaboração de listagens de eliminação, de planos de destinação, de editais de ciência de eliminação e de termos de eliminação.'),
(433, '061.522', '', ' TRANSFERÊNCIA', '', '', '', ' Incluem-se documentos referentes aos procedimentos para controle da passagem de documentos do arquivo corrente (arquivos setoriais) para o arquivo intermediário (arquivo central ou geral), tais como: listagem descritiva do acervo, guia e termo de transferência.'),
(434, '061.523', '', ' RECOLHIMENTO', '', '', '', ' Incluem-se documentos referentes aos procedimentos para controle da passagem de documentos do arquivo intermediário para o arquivo permanente, tais como: listagem descritiva do acervo, guia e termo de recolhimento.'),
(435, '062', '', ' GESTÃO DE ACERVOS BIBLIOGRÁFICO E MUSEOLÓGICO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição, incorporação, processamento técnico, inventário e desincorporação de acervos bibliográfico e museológico.'),
(436, '062.1', '', ' AQUISIÇÃO E INCORPORAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à aquisição, no Brasil e exterior, e à incorporação de acervos bibliográfico e museológico pelo órgão e entidade.'),
(437, '062.11', '', ' COMPRA', '', '', '', ' Incluem-se documentos referentes à aquisição definitiva de acervos bibliográfico e museológico por compra e por importação, bem como aqueles referentes à assinatura de periódicos e de bases de dados. Nota: A compra poderá ocorrer nas modalidades de licitação, dispensa de licitação e inexigibilidade de licitação.'),
(438, '062.12', '', ' DOAÇÃO', '', '', '', ' Incluem-se documentos referentes à aquisição definitiva de acervos bibliográfico e museológico por doação. Quanto à doação da produção editorial do órgão e entidade, classificar no código 065.3.'),
(439, '062.13', '', ' PERMUTA', '', '', '', ' Incluem-se documentos referentes à aquisição definitiva de acervos bibliográfico e museológico por permuta. Quanto à permuta da produção editorial do órgão e entidade, classificar no código 065.3.'),
(440, '062.2', '', ' PROCESSAMENTO TÉCNICO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos procedimentos adotados para o tratamento técnico de acervos bibliográfico e museológico do órgão e entidade.'),
(441, '062.21', '', ' REGISTRO', '', '', '', ' Incluem-se documentos referentes ao registro de entrada do item bibliográfico ou museológico incorporado aos acervos.'),
(442, '062.22', '', ' CATALOGAÇÃO, CLASSIFICAÇÃO E INDEXAÇÃO', '', '', '', ' Incluem-se documentos referentes à identificação, classificação e descrição do item bibliográfico ou museológico, cujas informações subsidiarão a elaboração das fichas catalográficas.'),
(443, '062.3', '', ' INVENTÁRIO', '', '', '', ' Incluem-se documentos referentes ao controle de itens dos acervos bibliográfico e museológico que visam apoiar as atividades de alienação, desincorporação e baixa, bem como os relatórios de inventário e os livros de tombo. Quanto aos inventários de material permanente e de consumo, classificar nos códigos e 036.2, respectivamente.'),
(444, '062.4', '', ' DESINCORPORAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à desincorporação definitiva de itens dos acervos bibliográfico e museológico pelo órgão e entidade.'),
(445, '062.41', '', ' DOAÇÃO', '', '', '', ' Incluem-se documentos referentes à doação de itens de acervos bibliográfico e museológico considerados inadequados e obsoletos, bem como daqueles que possuem vários exemplares.'),
(446, '062.42', '', ' DESCARTE', '', '', '', ' Incluem-se documentos referentes ao descarte de itens dos acervos bibliográfico e museológico que se encontram infectados, deteriorados e mutilados.'),
(447, '063', '', ' CONTROLE DE ACESSO E DE MOVIMENTAÇÃO DE ACERVOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao acesso, às consultas e aos empréstimos de documentos arquivísticos, bibliográficos e museológicos, bem como aqueles referentes ao controle da movimentação dos acervos.'),
(448, '063.1', '', ' CONSULTAS', '', '', '', ' Incluem-se documentos referentes ao acesso e ao controle das consultas aos documentos arquivísticos, bibliográficos e museológicos, bem como aqueles referentes ao registro dos consulentes e usuários. Quanto aos pedidos de acesso à informação e aos documentos institucionais, encaminhados ao SIC, classificar no código 002.11.'),
(449, '063.2', '', ' EMPRÉSTIMOS', '', '', '', ' Incluem-se documentos referentes ao controle dos empréstimos de documentos arquivísticos, bibliográficos e museológicos.'),
(450, '063.3', '', ' MOVIMENTAÇÃO DE ACERVOS', '', '', '', ' Incluem-se documentos referentes à gestão de depósitos e ao controle da localização e da movimentação dos documentos arquivísticos, bibliográficos e museológicos entre as áreas de armazenamento e as áreas destinadas às consultas.'),
(451, '063.4', '', ' MUDANÇA DE ACERVOS', '', '', '', ' Incluem-se documentos referentes ao controle da mudança de acervos arquivísticos, bibliográficos e museológicos, distinta da movimentação de acervos, motivado pela necessidade de mudança de imóvel ou dentro do mesmo imóvel.'),
(452, '064', '', ' CONSERVAÇÃO E PRESERVAÇÃO DE ACERVOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à desinfestação, higienização e controle das áreas de armazenamento, à encadernação e à reformatação e restauração dos documentos.'),
(453, '064.01', '', ' AVALIAÇÃO E MONITORAMENTO', '', '', '', ' Incluem-se documentos referentes à elaboração de diagnósticos, relatórios e pareceres técnicos sobre as condições de conservação e preservação de acervos e fichas de registro do tratamento adotado para a restauração ou encadernação de documentos, bem como aqueles referentes ao plano de prevenção de riscos e à definição dos critérios adotados para a reformatação de acervos.'),
(454, '064.1', '', ' DESINFESTAÇÃO E HIGIENIZAÇÃO', '', '', '', ' Incluem-se documentos referentes aos procedimentos para desinfestação e higienização das áreas de armazenamento de acervos.'),
(455, '064.2', '', ' CONTROLE DAS ÁREAS DE ARMAZENAMENTO', '', '', '', ' Incluem-se documentos referentes aos procedimentos para controle da climatização e da segurança dos depósitos de guarda. Quanto à ocorrência de sinistros em imóveis do órgão e entidade, classificar no código 046.3.'),
(456, '064.3', '', ' REFORMATAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos procedimentos para a adoção da reformatação de acervos. Quanto ao fornecimento de cópias de documentos, classificar no código 069.2.'),
(457, '064.31', '', ' MICROFILMAGEM', '', '', '', ' Incluem-se documentos referentes aos procedimentos adotados para microfilmagem de documentos.'),
(458, '064.32', '', ' DIGITALIZAÇÃO', '', '', '', ' Incluem-se documentos referentes aos procedimentos adotados para digitalização de documentos.'),
(459, '064.4', '', ' RESTAURAÇÃO', '', '', '', ' Incluem-se documentos referentes aos procedimentos adotados para realização de restauração de documentos deteriorados ou danificados, bem como aqueles referentes aos serviços de encadernação.'),
(460, '065', '', ' PRODUÇÃO EDITORIAL', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes aos projetos de produção editorial do órgão e entidade.'),
(461, '065.1', '', ' EDIÇÃO. COEDIÇÃO', '', '', '', ' Incluem-se documentos referentes à edição e à coedição de livros, periódicos e vídeos produzidos pelo órgão e entidade, bem como aqueles referentes às autorizações de direitos autorais e à solicitação do registro internacional para identificação de livros (ISBN) e do registro internacional para identificação de publicações seriadas (ISSN).'),
(462, '065.2', '', ' EDITORAÇÃO E PROGRAMAÇÃO VISUAL', '', '', '', ' Incluem-se documentos referentes à edição de livros, periódicos e vídeos, à composição, copidesque, tradução e revisão de textos e à execução de impressão e serviços gráficos, bem como aqueles referentes à elaboração de page views.'),
(463, '065.3', '', ' PROMOÇÃO, DIVULGAÇÃO E DISTRIBUIÇÃO', '', '', '', ' Incluem-se documentos referentes à promoção, à divulgação, à doação e à permuta da produção editorial do órgão e entidade. Quanto à aquisição de acervos bibliográficos por doação e permuta, classificar nos códigos 062.12 e 062.13, respectivamente.'),
(464, '066', '', ' GESTÃO DE TECNOLOGIA DA INFORMAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao desenvolvimento de programas, instalação de equipamentos, implantação e controle de sistemas informatizados, bem como aqueles referentes à administração da infraestrutura tecnológica.'),
(465, '066.1', '', ' DESENVOLVIMENTO E CONTROLE DE SISTEMAS INFORMATIZADOS', '', '', '', ' Incluem-se documentos referentes ao desenvolvimento, implantação, manutenção e controle de programas e sistemas informatizados, tais como: manual de usuário, código fonte, especificação de requisitos, diagramas, desenhos de telas, gerência de configuração e projetos de bancos de dados.'),
(466, '066.2', '', ' INSTALAÇÃO DE EQUIPAMENTOS', '', '', '', ' Incluem-se documentos referentes à instalação de equipamentos para a implementação de programas e sistemas, tais como: manual dos equipamentos, termo de garantia, manual de usuário, licença e registro de uso.'),
(467, '066.3', '', ' ADMINISTRAÇÃO DA INFRAESTRUTURA TECNOLÓGICA', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao desenvolvimento do projeto de infraestrutura tecnológica, bem como aqueles referentes à manutenção, gerenciamento e uso da infraestrutura.'),
(468, '066.31', '', ' PROJETO DE MANUTENÇÃO', '', '', '', ' Incluem-se documentos referentes ao projeto de manutenção da infraestrutura tecnológica, tais como: projeto da rede e configuração dos servidores.'),
(469, '066.32', '', ' GERENCIAMENTO E USO', '', '', '', ' Incluem-se documentos referentes ao gerenciamento e ao uso da infraestrutura tecnológica, tais como: gerenciamento de discos, registro e controle das permissões de acesso à rede, credenciais de segurança, gerenciamento de correio eletrônico, relatórios de desempenho, log de dados e controle de backup.'),
(470, '066.4', '', ' ADMINISTRAÇÃO DE BANCO DE DADOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à instalação, configuração e gerenciamento de banco de dados.'),
(471, '066.41', '', ' INSTALAÇÃO E CONFIGURAÇÃO', '', '', '', ' Incluem-se documentos referentes à instalação e configuração de banco de dados, estrutura, dicionário de dados, script de criação e stored procedures.'),
(472, '066.42', '', ' GERENCIAMENTO E USO', '', '', '', ' Incluem-se documentos referentes ao gerenciamento de banco de dados, tais como: registro e controle das permissões de acesso ao banco de dados e relatórios de desempenho.'),
(473, '066.9', '', ' OUTRAS AÇÕES REFERENTES À GESTÃO DE TECNOLOGIA DA INFORMAÇÃO', '', '', '', ' Na subdivisão deste descritor classificam-se documentos referentes a outras ações de gestão de tecnologia da informação não contempladas nos descritores anteriores.'),
(474, '066.91', '', ' CONTROLE DO SUPORTE TÉCNICO', '', '', '', ' Incluem-se documentos referentes ao controle do suporte técnico prestado para utilização dos sistemas informatizados. Quanto a contratação de prestação de serviços por empresas terceirizadas para a gestão de sistemas e de infraestrutura tecnológica, classificar no código 018.1.'),
(475, '067', '', ' CONTRATAÇÃO DE PRESTAÇÃO DE SERVIÇOS', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(476, '069', '', ' OUTRAS AÇÕES REFERENTES À GESTÃO DA DOCUMENTAÇÃO E DA INFORMAÇÃO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes a outras ações de gestão da documentação e da informação não contempladas nos descritores anteriores.'),
(477, '069.1', '', ' TRATAMENTO TÉCNICO DA DOCUMENTAÇÃO ARQUIVÍSTICA PERMANENTE', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes ao tratamento técnico da documentação arquivística de guarda permanente.'),
(478, '069.11', '', ' ARRANJO E DESCRIÇÃO', '', '', '', ' Incluem-se documentos referentes à metodologia adotada para o desenvolvimento das atividades de identificação, arranjo e descrição dos documentos arquivísticos de guarda permanente.'),
(479, '069.12', '', ' ELABORAÇÃO DE INSTRUMENTOS DE PESQUISA', '', '', '', ' Incluem-se documentos referentes à elaboração dos instrumentos que permitem a identificação e a localização dos documentos arquivísticos de guarda permanente, tais como: catálogo, guia, inventário, listagem descritiva do acervo, repertório e tabela de equivalência.'),
(480, '069.2', '', ' FORNECIMENTO DE CÓPIAS DE DOCUMENTOS', '', '', '', ' Incluem-se documentos referentes à requisição e ao controle da reprodução de documentos por impressão e por outros serviços reprográficos. Quanto à reformatação de acervos por microfilmagem e digitalização, classificar nas subdivisões do código 064.3.'),
(481, '069.3', '', ' PUBLICAÇÃO DE MATÉRIAS', '', '', '', ' Incluem-se documentos referentes à publicação de matérias, da competência do órgão e entidade, em diários oficiais, em periódicos de grande circulação e em boletins internos e similares. Quanto aos boletins administrativos e de serviço, classificar no código 010.01. Quanto aos boletins de pessoal, classificar no código 020.01.'),
(482, '070', '', ' GESTÃO DOS SERVIÇOS POSTAIS E DE TELECOMUNICAÇÕES', '', '', '', ' Esta subclasse contempla documentos referentes às atividades desenvolvidas pelo órgão e entidade para a gestão dos serviços postais e de telecomunicações (radiofrequência, telex, telefonia, fax e transmissão de dados, voz e imagem), bem como aqueles referentes à serviços de instalação, manutenção e reparo e à autorização e controle de uso.'),
(483, '070.01', '', ' NORMATIZAÇÃO. REGULAMENTAÇÃO', '', '', '', ' Incluem-se documentos referentes às determinações legais, aos atos e instruções normativas, aos procedimentos operacionais e às decisões de caráter geral sobre a gestão dos serviços postais e de telecomunicações do órgão e entidade.'),
(484, '071', '', ' CONTRATAÇÃO DE PRESTAÇÃO DE SERVIÇOS', '', '', '', ' Os descritores 071.1, 071.2, 071.3, 071.4 e 071.5 foram suprimidos em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(485, '071.1', '', ' SERVIÇO POSTAL', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(486, '071.2', '', ' SERVIÇO DE RADIOFREQUÊNCIA', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.');
INSERT INTO `ttddnew1` (`id`, `symbol`, `kind`, `assunto`, `fase_corrente`, `fase_intermediaria`, `destinacao`, `observacoes`) VALUES
(487, '071.3', '', ' SERVIÇO DE TELEX', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(488, '071.4', '', ' SERVIÇO DE TELEFONIA. SERVIÇO DE FAX', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(489, '071.5', '', ' SERVIÇO DE TRANSMISSÃO DE DADOS, VOZ E IMAGEM', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1.'),
(490, '072', '', ' EXECUÇÃO DE SERVIÇOS PELO ÓRGÃO E ENTIDADE', '', '', '', ' Incluem-se documentos referentes à instalação, manutenção e reparo dos serviços de telecomunicações, executados com meios próprios pelo órgão e entidade, não gerando contratação de empresas terceirizadas ou pagamento de despesas.'),
(491, '073', '', ' AUTORIZAÇÃO E CONTROLE DE USO', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à autorização e controle de uso dos serviços de radiofrequência, de telex, de telefonia, de fax e de transmissão de dados, voz e imagem.'),
(492, '073.1', '', ' SERVIÇO DE RADIOFREQUÊNCIA', '', '', '', ' Incluem-se documentos referentes à autorização e ao controle do uso dos serviços de radiofrequência.'),
(493, '073.2', '', ' SERVIÇO DE TELEX', '', '', '', ' Incluem-se documentos referentes à autorização e ao controle do uso dos serviços de telex.'),
(494, '073.3', '', ' SERVIÇO DE TELEFONIA. SERVIÇO DE FAX', '', '', '', ' Nas subdivisões desse descritor classificam-se os documentos referentes à autorização e ao controle da utilização dos serviços de telefonia e de fax.'),
(495, '073.31', '', ' TRANSFERÊNCIA DE PROPRIEDADE OU TITULARIDADE', '', '', '', ' Incluem-se documentos referentes à transferência de propriedade ou titularidade das linhas telefônicas e dos serviços telefônicos fixo ou móvel.'),
(496, '073.32', '', ' REGISTRO DE LIGAÇÕES', '', '', '', ' Incluem-se documentos referentes à autorização e ao controle das ligações interurbanas, internacionais e de telefonia móvel.'),
(497, '073.33', '', ' DIVULGAÇÃO DE LISTAS TELEFÔNICAS INTERNAS', '', '', '', ' Incluem-se as listagens de servidores e unidades administrativas, com os respectivos ramais internos, elaboradas com o objetivo de facilitar e agilizar a comunicação interna no órgão e entidade.'),
(498, '073.4', '', ' SERVIÇO DE TRANSMISSÃO DE DADOS, VOZ E IMAGEM', '', '', '', ' Incluem-se documentos referentes à autorização e ao controle do uso dos serviços de acesso à internet, assinatura de televisão e videoconferência.'),
(499, '080', '', ' PESSOAL MILITAR', '', '', '', ' – Anexo I'),
(500, '900', '', ' ADMINISTRAÇÃO DE ATIVIDADES ACESSÓRIAS', '', '', '', ' Esta classe contempla documentos referentes ao desenvolvimento de atividades complementares, normalmente, vinculadas às atividadesmeio, mas que não são essenciais para o funcionamento e cumprimento das competências finalísticas do órgão e entidade.'),
(501, '910', '', ' GESTÃO DE EVENTOS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes à gestão dos eventos promovidos e realizados pelo órgão e entidade, como: solenidades, comemorações, homenagens, congressos, conferências, seminários, simpósios, jornadas, oficinas, encontros, convenções, ciclos de palestras, mesas redondas, feiras, salões, exposições, mostras, exibição de filmes e vídeos, lançamentos de livros, festas e concursos culturais. Quanto às atividades de comunicação social no âmbito externo, classificar nas subdivisões do código 019.11.'),
(502, '910.01', '', ' NORMATIZAÇÃO. REGULAMENTAÇÃO', '', '', '', ' Incluem-se documentos referentes às determinações legais, aos atos e instruções normativas, aos procedimentos operacionais e às decisões de caráter geral sobre a gestão de eventos promovidos e realizados pelo órgão e entidade.'),
(503, '911', '', ' PLANEJAMENTO E PROGRAMAÇÃO', '', '', '', ' Incluem-se documentos referentes ao planejamento e à programação do evento, tais como: a definição do tema, a programa prévio e final, a lista de palestrantes e convidados, o cronograma das atividades (dias, horários e sessões), as instruções para participantes e ouvintes, os contatos com parcerias, patrocinadores e fornecedores e a disponibilização de informações turísticas e sobre hospedagem, alimentação e transporte.'),
(504, '912', '', ' DIVULGAÇÃO', '', '', '', ' Incluem-se documentos referentes à divulgação dos eventos, tais como: fôlderes, cartazes, folhetos, chamadas nas redes sociais e anúncios nas mídias de comunicação impressas e eletrônicas.'),
(505, '913', '', ' INSCRIÇÃO E CONTROLE DE FREQUÊNCIA', '', '', '', ' Incluem-se fichas de inscrição, cadastro ou relação de inscritos e listas de presença dos participantes nos eventos.'),
(506, '914', '', ' EMISSÃO DE CERTIFICADOS', '', '', '', ' Incluem-se documentos referentes à expedição, registro e controle da entrega do certificado aos participantes nos eventos.'),
(507, '915', '', ' AVALIAÇÃO DOS RESULTADOS', '', '', '', ' Incluem-se documentos referentes à avaliação dos resultados alcançados pelos eventos, tais como: formulários de avaliação e relatórios parciais e finais.'),
(508, '916', '', ' HABILITAÇÃO, JULGAMENTO E RECURSOS', '', '', '', ' Incluem-se documentos referentes à habilitação dos candidatos inscritos nos prêmios e nos concursos culturais, ao julgamento dos trabalhos concorrentes, ao desempenho dos participantes e aos recursos apresentados aos resultados do julgamento, bem como os trabalhos inscritos e as atas de julgamento.'),
(509, '917', '', ' PREMIAÇÃO', '', '', '', ' Incluem-se documentos referentes a entrega da premiação aos candidatos vencedores dos concursos culturais, tais como: termo de entrega e recebimento e termo de cessão de direitos autorais.'),
(510, '918', '', ' CONTRATAÇÃO DE PRESTAÇÃO DE SERVIÇOS', '', '', '', ' Esse descritor foi suprimido em revisão do presente instrumento em 2022. Todos os documentos relativos à contratação de pessoa jurídica para prestação de serviços devem ser classificados no código 018.1. Quanto ao uso de dependências do órgão e entidade, por terceiros, mediante locação, arrendamento mercantil (leasing) e sublocação, classificar no código 041.61.'),
(511, '919', '', ' OUTRAS AÇÕES REFERENTES À GESTÃO DE EVENTOS', '', '', '', ' Na subdivisão deste descritor classificam-se documentos referentes a outras ações de gestão de eventos não contempladas nos descritores anteriores.'),
(512, '919.1', '', ' PARTICIPAÇÃO EM EVENTOS PROMOVIDOS E REALIZADOS POR OUTRAS INSTITUIÇÕES', '', '', '', ' Incluem-se documentos referentes à participação do servidor em eventos promovidos por outras instituições, tais como: comprovantes da inscrição e da participação no evento, discursos, palestras e trabalhos apresentados. Quanto aos documentos comprobatórios de participação do servidor, que deverão integrar o assentamento funcional, classificar nas subdivisões do código 020.1.'),
(513, '920', '', ' PROMOÇÃO DE VISITAS', '', '', '', ' Nas subdivisões deste descritor classificam-se os documentos referentes à promoção de visitas culturais, educativas e técnicas ao órgão e entidade.'),
(514, '920.01', '', ' NORMATIZAÇÃO. REGULAMENTAÇÃO', '', '', '', ' Incluem-se documentos referentes às determinações legais, aos atos e instruções normativas, aos procedimentos operacionais e às decisões de caráter geral sobre a promoção de visitas ao órgão e entidade.'),
(515, '921', '', ' PROGRAMAÇÃO DE VISITAS', '', '', '', ' Incluem-se documentos referentes ao atendimento de solicitação de visitas ao órgão e entidade, bem como aqueles referentes ao planejamento e à programação de visitas monitoradas, dirigidas a diferentes públicos.'),
(516, '922', '', ' CONTROLE DE VISITAS E VISITANTES', '', '', '', ' Incluem-se documentos referentes ao controle da entrada de visitantes e ao acompanhamento dos mesmos por ocasião das visitas.'),
(517, '990', '', ' OUTRAS AÇÕES REFERENTES À ADMINISTRAÇÃO DE ATIVIDADES ACESSÓRIAS', '', '', '', ' Nas subdivisões deste descritor classificam-se documentos referentes às comunicações ocasionais e genéricas produzidas ou recebidas pelo órgão e entidade no relacionamento com outras instituições públicas e privadas e que não dizem respeito ao desenvolvimento de suas atividades específicas.'),
(518, '991', '', ' GESTÃO DE COMUNICAÇÕES EVENTUAIS', '', '', '', ' Incluem-se comunicados, informes, pedidos, solicitações e oferecimentos, trocados entre o órgão e entidade e demais instituições públicas e privadas, que não tiveram solução de continuidade, como por exemplo: apresentação e recomendação de pessoas e de profissionais, comunicado de falecimento e envio de pêsames, informe de posse, afastamento e mudança de titular, comunicados de alterações de telefones e endereços, informe de luto oficial, envio de cumprimentos e felicitações, recebimento de convites para solenidade e eventos diversos. Quanto às solicitações de informações efetuadas pelos cidadãos por meio do SIC, classificar nas subdivisões do código 002.1. Quanto aos elogios e reclamações recebidos pela ouvidoria e outros canais de comunicação, classificar no código 002.2.'),
(519, '992', '', ' RELACIONAMENTO COM ASSOCIAÇÕES CULTURAIS, DE AMIGOS E DE SERVIDORES', '', '', '', ' Incluem-se documentos referentes ao relacionamento do órgão e entidade com associações culturais, de amigos e de servidores.');
